import { Component, OnInit, signal, ViewChild } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterOutlet } from '@angular/router';
import { ChangeDetectionStrategy } from '@angular/core';
import { SidebarComponent, HeaderComponent, FooterComponent } from '@app/shared/components';
import { UserService, UserProfile } from '@app/core/services/user.service';

@Component({
  selector: 'app-root',
  standalone: true,
  imports: [CommonModule, RouterOutlet, SidebarComponent, HeaderComponent, FooterComponent],
  changeDetection: ChangeDetectionStrategy.OnPush,
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent implements OnInit {
  @ViewChild(SidebarComponent) sidebar!: SidebarComponent;

  profile = signal<UserProfile | null>(null);

  constructor(private userService: UserService) {}

  ngOnInit(): void {
    this.userService.getProfile().subscribe(profile => {
      this.profile.set(profile);
    });
  }

  navigateToHire(): void {
    if (this.sidebar) {
      this.sidebar.currentView.set('Hire');
    }
  }

  generatePDF(): void {
    console.log('Printing PDF...');
    window.print();
  }
}
<div class="flex h-screen bg-slate-950 overflow-hidden">
  <!-- Sidebar -->
  <app-sidebar></app-sidebar>

  <!-- Main Content Area -->
  <div class="flex-1 flex flex-col overflow-hidden">
    <!-- Header -->
    <app-header></app-header>

    <!-- Router Outlet -->
    <main class="flex-1 overflow-y-auto scrollbar-thin scrollbar-thumb-slate-700 scrollbar-track-slate-900 px-6 py-6 md:px-10 md:py-8">
      <router-outlet></router-outlet>
    </main>

    <!-- Footer -->
    <app-footer></app-footer>
  </div>
</div>
