import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { Router, ActivatedRoute } from '@angular/router';
import { AuthService } from '@app/core/services/auth.service';

@Component({
  selector: 'app-login',
  standalone: true,
  imports: [CommonModule, FormsModule],
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.scss']
})
export class LoginComponent implements OnInit {
  username = '';
  password = '';
  loading = false;
  error: string | null = null;
  showPassword = false;
  returnUrl: string | null = null;

  constructor(
    private authService: AuthService,
    private router: Router,
    private route: ActivatedRoute
  ) {}

  ngOnInit(): void {
    // Get return URL from route parameters or default to '/'
    this.returnUrl = this.route.snapshot.queryParams['returnUrl'] || '/management';

    // If already logged in, redirect
    if (this.authService.isLoggedIn()) {
      this.router.navigateByUrl(this.returnUrl || '/management');
    }
  }

  onLogin(): void {
    if (!this.username.trim() || !this.password.trim()) {
      this.error = 'Please enter both username and password';
      return;
    }

    this.loading = true;
    this.error = null;

    this.authService.login(this.username, this.password).subscribe({
      next: (response: any) => {
        this.loading = false;
        // Navigate to management dashboard or return URL
        this.router.navigateByUrl(this.returnUrl || '/management');
      },
      error: (err: any) => {
        this.loading = false;
        this.error = 'Login failed. Please check your credentials.';
        this.password = '';
      }
    });
  }

  togglePasswordVisibility(): void {
    this.showPassword = !this.showPassword;
  }

  onKeyPress(event: KeyboardEvent): void {
    if (event.key === 'Enter') {
      this.onLogin();
    }
  }

  clearError(): void {
    this.error = null;
  }
}
<div class="login-container">
  <!-- Background Animation -->
  <div class="background-animation">
    <div class="blob blob-1"></div>
    <div class="blob blob-2"></div>
    <div class="blob blob-3"></div>
  </div>

  <!-- Error Overlay Alert -->
  <div *ngIf="error" class="error-overlay">
    <div class="error-alert">
      <div class="error-icon">
        <i class="fas fa-exclamation-circle"></i>
      </div>
      <div class="error-content">
        <h2>Login Failed</h2>
        <p>{{ error }}</p>
      </div>
      <button class="error-close" (click)="clearError()" title="Close">
        <i class="fas fa-times"></i>
      </button>
    </div>
  </div>

  <div class="login-card">
    <!-- Header -->
    <div class="login-header">
      <div class="logo-circle">
        <i class="fas fa-lock"></i>
      </div>
      <h1>Kojo.Stack</h1>
      <p>Administration Login</p>
    </div>

    <!-- Login Form -->
    <form class="login-form" (ngSubmit)="onLogin()">
      <!-- Username Input -->
      <div class="form-group">
        <label for="username">
          <i class="fas fa-user"></i> Username
        </label>
        <input
          id="username"
          type="text"
          class="form-control"
          [(ngModel)]="username"
          name="username"
          placeholder="Enter your username"
          [disabled]="loading"
          required
          autofocus
        />
      </div>

      <!-- Password Input -->
      <div class="form-group">
        <label for="password">
          <i class="fas fa-lock"></i> Password
        </label>
        <div class="password-input-group">
          <input
            id="password"
            [type]="showPassword ? 'text' : 'password'"
            class="form-control"
            [(ngModel)]="password"
            name="password"
            placeholder="Enter your password"
            [disabled]="loading"
            (keypress)="onKeyPress($event)"
            required
          />
          <button
            type="button"
            class="toggle-password"
            (click)="togglePasswordVisibility()"
            [disabled]="loading"
            tabindex="-1"
          >
            <i [class]="showPassword ? 'fas fa-eye-slash' : 'fas fa-eye'"></i>
          </button>
        </div>
      </div>

      <!-- Submit Button -->
      <button
        type="submit"
        class="btn btn-login"
        [disabled]="loading"
        [class.loading]="loading"
      >
        <span *ngIf="!loading">
          <i class="fas fa-sign-in-alt"></i> Sign In
        </span>
        <span *ngIf="loading">
          <i class="fas fa-spinner fa-spin"></i> Logging in...
        </span>
      </button>
    </form>

    <!-- Footer -->
    <div class="login-footer">
      <p class="hint">
        <i class="fas fa-info-circle"></i>
        Use admin credentials to access the admin dashboard
      </p>
    </div>
  </div>

  <!-- Background Animation -->
  <div class="background-animation">
    <div class="blob blob-1"></div>
    <div class="blob blob-2"></div>
    <div class="blob blob-3"></div>
  </div>
</div>
