import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { Router, ActivatedRoute } from '@angular/router';
import { AuthService } from '@app/core/services/auth.service';

@Component({
  selector: 'app-login',
  standalone: true,
  imports: [CommonModule, FormsModule],
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.scss']
})
export class LoginComponent implements OnInit {
  username = '';
  password = '';
  loading = false;
  error: string | null = null;
  errorType: 'validation' | 'auth' | 'network' = 'auth';
  showPassword = false;
  returnUrl: string | null = null;
  showErrorAlert = false;

  constructor(
    private authService: AuthService,
    private router: Router,
    private route: ActivatedRoute
  ) {}

  ngOnInit(): void {
    // Get return URL from route parameters or default to '/'
    this.returnUrl = this.route.snapshot.queryParams['returnUrl'] || '/management';

    // If already logged in, redirect
    if (this.authService.isLoggedIn()) {
      this.router.navigateByUrl(this.returnUrl || '/management');
    }
  }

  onLogin(): void {
    if (!this.username.trim() || !this.password.trim()) {
      this.showError('Please enter both username and password', 'validation');
      return;
    }

    this.loading = true;
    this.error = null;
    this.showErrorAlert = false;

    this.authService.login(this.username, this.password).subscribe({
      next: (response) => {
        this.loading = false;
        // Navigate to management dashboard or return URL
        this.router.navigateByUrl(this.returnUrl || '/management');
      },
      error: (err) => {
        this.loading = false;
        const errorMessage = this.extractErrorMessage(err);
        this.showError(errorMessage, 'auth');
        this.password = '';
      }
    });
  }

  /**
   * Extract meaningful error message from HTTP error response
   */
  private extractErrorMessage(err: any): string {
    if (err.error?.message) {
      return err.error.message;
    }
    if (err.error?.error) {
      return err.error.error;
    }
    if (err.status === 0) {
      return 'Unable to connect to the server. Please check your network connection.';
    }
    if (err.status === 401 || err.status === 403) {
      return 'Invalid username or password. Please try again.';
    }
    if (err.status === 500) {
      return 'Server error. Please try again later.';
    }
    return 'Login failed. Please check your credentials and try again.';
  }

  /**
   * Display error message with type classification
   */
  private showError(message: string, type: 'validation' | 'auth' | 'network'): void {
    this.error = message;
    this.errorType = type;
    this.showErrorAlert = true;
  }

  togglePasswordVisibility(): void {
    this.showPassword = !this.showPassword;
  }

  onKeyPress(event: KeyboardEvent): void {
    if (event.key === 'Enter') {
      this.onLogin();
    }
  }

  clearError(): void {
    this.error = null;
    this.showErrorAlert = false;
  }
}
<div class="login-container">
  <div class="login-card">
    <!-- Header -->
    <div class="login-header">
      <div class="logo-circle">
        <i class="fas fa-lock"></i>
      </div>
      <h1>Kojo.Stack</h1>
      <p>Administration Login</p>
    </div>

    <!-- Error Message Alert -->
    <div 
      *ngIf="showErrorAlert && error" 
      class="alert alert-error"
      [@slideDown]
      role="alert"
      aria-live="assertive"
    >
      <div class="alert-icon">
        <i class="fas fa-exclamation-circle"></i>
      </div>
      <div class="alert-content">
        <div class="alert-title" [ngSwitch]="errorType">
          <span *ngSwitchCase="'validation'">Validation Error</span>
          <span *ngSwitchCase="'auth'">Authentication Failed</span>
          <span *ngSwitchCase="'network'">Connection Error</span>
          <span *ngSwitchDefault>Error</span>
        </div>
        <div class="alert-message">{{ error }}</div>
      </div>
      <button 
        type="button"
        class="close-btn" 
        (click)="clearError()"
        aria-label="Close error message"
      >
        <i class="fas fa-times"></i>
      </button>
    </div>

    <!-- Login Form -->
    <form class="login-form" (ngSubmit)="onLogin()">
      <!-- Username Input -->
      <div class="form-group">
        <label for="username">
          <i class="fas fa-user"></i> Username
        </label>
        <input
          id="username"
          type="text"
          class="form-control"
          [(ngModel)]="username"
          name="username"
          placeholder="Enter your username"
          [disabled]="loading"
          required
          autofocus
        />
      </div>

      <!-- Password Input -->
      <div class="form-group">
        <label for="password">
          <i class="fas fa-lock"></i> Password
        </label>
        <div class="password-input-group">
          <input
            id="password"
            [type]="showPassword ? 'text' : 'password'"
            class="form-control"
            [(ngModel)]="password"
            name="password"
            placeholder="Enter your password"
            [disabled]="loading"
            (keypress)="onKeyPress($event)"
            required
          />
          <button
            type="button"
            class="toggle-password"
            (click)="togglePasswordVisibility()"
            [disabled]="loading"
            tabindex="-1"
          >
            <i [class]="showPassword ? 'fas fa-eye-slash' : 'fas fa-eye'"></i>
          </button>
        </div>
      </div>

      <!-- Submit Button -->
      <button
        type="submit"
        class="btn btn-login"
        [disabled]="loading"
        [class.loading]="loading"
      >
        <span *ngIf="!loading">
          <i class="fas fa-sign-in-alt"></i> Sign In
        </span>
        <span *ngIf="loading">
          <i class="fas fa-spinner fa-spin"></i> Logging in...
        </span>
      </button>
    </form>

    <!-- Footer -->
    <div class="login-footer">
      <p class="hint">
        <i class="fas fa-info-circle"></i>
        Use admin credentials to access the admin dashboard
      </p>
    </div>
  </div>

  <!-- Background Animation -->
  <div class="background-animation">
    <div class="blob blob-1"></div>
    <div class="blob blob-2"></div>
    <div class="blob blob-3"></div>
  </div>
</div>
