export class EducationDashboardComponent {
  constructor(educationService) {
    this.educationService = educationService;
    this.modelName = 'Education';
    this.filteredItems = [];
    this.selectedItem = null;
    this.detailItem = null;
    this.isCreating = false;
    this.isUpdating = false;
    this.isDeleting = false;
    this.isViewing = false;
    this.currentAction = 'list';
    this.loading = false;
    this.error = null;
    this.selectedType = 'All';
    this.typeOptions = ['University Bachelor Education', 'Pre University School', 'Secondary Advance Level', 'Secondary GCE Ordinary Level', 'Professional Workshop', 'Professional Study', 'Certification'];
    this.formData = {
      institution: '',
      subjects: [],
      type: 'University Bachelor Education',
      duration: ''
    };
    this.subjectsInput = '';
    this.education = signal([]);
  }
  ngOnInit() {
    this.loadEducation();
  }
  loadEducation() {
    this.loading = true;
    this.educationService.getAll().subscribe({
      next: education => {
        if (education && education.length > 0) {
          this.education.set(education);
          this.filterByType();
          this.loading = false;
        } else {
          this.loadMockEducation();
        }
      },
      error: () => {
        this.loadMockEducation();
      }
    });
  }
  loadMockEducation() {
    this.educationService.getEducation().subscribe(education => {
      this.education.set(education);
      this.filterByType();
      this.loading = false;
    });
  }
  filterByType() {
    if (this.selectedType === 'All') {
      this.filteredItems = [...this.education()];
    } else {
      this.filteredItems = this.education().filter(e => e.type === this.selectedType);
    }
  }
  onTypeChange() {
    this.filterByType();
  }
  selectItem(item) {
    this.selectedItem = item;
  }
  viewItem(item) {
    this.currentAction = 'view';
    this.isViewing = true;
    this.detailItem = item;
    this.error = null;
  }
  createItem() {
    this.currentAction = 'create';
    this.isCreating = true;
    this.selectedItem = null;
    this.formData = {
      institution: '',
      subjects: [],
      type: 'University Bachelor Education',
      duration: ''
    };
    this.subjectsInput = '';
    this.error = null;
  }
  editItem(item) {
    this.currentAction = 'update';
    this.isUpdating = true;
    this.selectedItem = item;
    this.formData = {
      ...item
    };
    this.subjectsInput = item.subjects?.join(', ') || '';
    this.error = null;
  }
  deleteItem(item) {
    this.currentAction = 'delete';
    this.isDeleting = true;
    this.selectedItem = item;
    this.error = null;
  }
  saveItem() {
