import { Component, OnInit, signal } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { RouterModule } from '@angular/router';
import { Education, EducationType } from '@app/core/models/education.model';
import { EducationService } from '@app/core/services/education.service';

@Component({
  selector: 'app-education-dashboard',
  standalone: true,
  imports: [CommonModule, FormsModule, RouterModule],
  templateUrl: './education-dashboard.component.html',
  styleUrls: ['./education-dashboard.component.scss']
})
export class EducationDashboardComponent implements OnInit {
  modelName = 'Education';
  filteredItems: Education[] = [];
  selectedItem: Education | null = null;
  detailItem: Education | null = null;
  isCreating = false;
  isUpdating = false;
  isDeleting = false;
  isViewing = false;
  currentAction: 'list' | 'create' | 'update' | 'delete' | 'view' = 'list';
  loading = false;
  error: string | null = null;
  selectedType: string = 'All';

  typeOptions: string[] = [
    'University Bachelor Education',
    'Pre University School',
    'Secondary Advance Level',
    'Secondary GCE Ordinary Level',
    'Professional Workshop',
    'Professional Study',
    'Certification'
  ];

  formData: Partial<Education> = {
    institution: '',
    subjects: [],
    type: 'University Bachelor Education',
    duration: ''
  };

  subjectsInput = '';
  education = signal<Education[]>([]);

  constructor(private educationService: EducationService) {}

  ngOnInit(): void {
    this.loadEducation();
  }

  loadEducation(): void {
    this.loading = true;
    this.educationService.getAll().subscribe({
      next: (education) => {
        if (education && education.length > 0) {
          this.education.set(education);
          this.filterByType();
          this.loading = false;
        } else {
          this.loadMockEducation();
        }
      },
      error: () => {
        this.loadMockEducation();
      }
    });
  }

  loadMockEducation(): void {
    this.educationService.getEducation().subscribe(education => {
      this.education.set(education);
      this.filterByType();
      this.loading = false;
    });
  }

  filterByType(): void {
    if (this.selectedType === 'All') {
      this.filteredItems = [...this.education()];
    } else {
      this.filteredItems = this.education().filter(e => e.type === this.selectedType);
    }
  }

  onTypeChange(): void {
    this.filterByType();
  }

  selectItem(item: Education): void {
    this.selectedItem = item;
  }

  viewItem(item: Education): void {
    this.currentAction = 'view';
    this.isViewing = true;
    this.detailItem = item;
    this.error = null;
  }

  createItem(): void {
    this.currentAction = 'create';
    this.isCreating = true;
    this.selectedItem = null;
    this.formData = {
      institution: '',
      subjects: [],
      type: 'University Bachelor Education',
      duration: ''
    };
    this.subjectsInput = '';
    this.error = null;
  }

  editItem(item: Education): void {
    this.currentAction = 'update';
    this.isUpdating = true;
    this.selectedItem = item;
    this.formData = { ...item };
    this.subjectsInput = item.subjects?.join(', ') || '';
    this.error = null;
  }

  deleteItem(item: Education): void {
    this.currentAction = 'delete';
    this.isDeleting = true;
    this.selectedItem = item;
    this.error = null;
  }

  saveItem(): void {
    this.loading = true;

    // Parse subjects from comma-separated string
    this.formData.subjects = this.subjectsInput
      .split(',')
      .map(s => s.trim())
      .filter(s => s.length > 0);

    const education = { ...this.formData } as Education;

    if (this.isCreating) {
      this.educationService.create(education).subscribe({
        next: () => {
          this.loadEducation();
          this.cancelAction();
        },
        error: (err) => {
          this.error = 'Failed to create education record';
          this.loading = false;
        }
      });
    } else if (this.isUpdating && this.selectedItem?.id) {
      this.educationService.update(this.selectedItem.id, education).subscribe({
        next: () => {
          this.loadEducation();
          this.cancelAction();
        },
        error: (err) => {
          this.error = 'Failed to update education record';
          this.loading = false;
        }
      });
    }
  }

  confirmDelete(): void {
    if (this.selectedItem?.id) {
      this.loading = true;
      this.educationService.delete(this.selectedItem.id).subscribe({
        next: () => {
          this.loadEducation();
          this.cancelAction();
        },
        error: (err) => {
          this.error = 'Failed to delete education record';
          this.loading = false;
        }
      });
    } else {
      this.cancelAction();
    }
  }

  cancelAction(): void {
    this.currentAction = 'list';
    this.isCreating = false;
    this.isUpdating = false;
    this.isDeleting = false;
    this.isViewing = false;
    this.selectedItem = null;
    this.detailItem = null;
    this.error = null;
    this.formData = {};
    this.subjectsInput = '';
    this.loading = false;
  }

  getTypeClass(type: string): string {
    if (type.includes('University') || type.includes('Bachelor')) return 'type-university';
    if (type.includes('Pre University')) return 'type-preuni';
    if (type.includes('Secondary')) return 'type-secondary';
    if (type.includes('Professional') || type.includes('Workshop')) return 'type-professional';
    if (type.includes('Certification')) return 'type-certification';
    return 'type-default';
  }

  getTypeIcon(type: string): string {
    if (type.includes('University') || type.includes('Bachelor')) return 'üéì';
    if (type.includes('Pre University')) return 'üìö';
    if (type.includes('Secondary')) return 'üè´';
    if (type.includes('Professional') || type.includes('Workshop')) return 'üíº';
    if (type.includes('Certification')) return 'üìú';
    return 'üìñ';
  }
}
<div class="model-dashboard">
  <!-- Back to Admin Button -->
  <div class="nav-header">
    <a routerLink="/management" class="btn btn-back">
      ‚Üê Back to Admin Dashboard
    </a>
  </div>

  <!-- Header -->
  <div class="dashboard-header">
    <h1>üéì {{ modelName }} Management</h1>
    <div class="header-actions">
      <select class="form-control type-filter" [(ngModel)]="selectedType" (change)="onTypeChange()">
        <option value="All">All Types</option>
        <option *ngFor="let type of typeOptions" [value]="type">{{ type }}</option>
      </select>
      <button 
        class="btn btn-primary" 
        (click)="createItem()"
        [disabled]="loading">
        + Add New {{ modelName }}
      </button>
    </div>
  </div>

  <!-- Error Message -->
  <div *ngIf="error" class="alert alert-error">
    {{ error }}
    <button class="close-btn" (click)="error = null">√ó</button>
  </div>

  <!-- Loading Indicator -->
  <div *ngIf="loading" class="loading">
    <div class="spinner"></div>
    Loading...
  </div>

  <!-- List View -->
  <div *ngIf="currentAction === 'list' && !loading" class="list-view">
    <div *ngIf="filteredItems.length === 0" class="empty-state">
      <p>No {{ modelName | lowercase }} records found</p>
      <button class="btn btn-primary" (click)="createItem()">Create First {{ modelName }}</button>
    </div>

    <div class="education-grid" *ngIf="filteredItems.length > 0">
      <div class="education-card" *ngFor="let item of filteredItems" 
           [ngClass]="getTypeClass(item.type)"
           [class.selected]="selectedItem?.institution === item.institution">
        <div class="card-header">
          <span class="type-badge" [ngClass]="getTypeClass(item.type)">{{ item.type }}</span>
        </div>
        <div class="card-body">
          <div class="education-icon">{{ getTypeIcon(item.type) }}</div>
          <h3>{{ item.institution }}</h3>
          <div class="education-duration">
            <span class="duration-icon">üìÖ</span>
            <span>{{ item.duration }}</span>
          </div>
          <div class="subjects-list">
            <span class="subject-tag" *ngFor="let subject of item.subjects?.slice(0, 3)">
              {{ subject }}
            </span>
            <span *ngIf="item.subjects && item.subjects.length > 3" class="subject-more">
              +{{ item.subjects.length - 3 }} more
            </span>
          </div>
        </div>
        <div class="card-actions">
          <button class="btn btn-sm btn-view" (click)="viewItem(item)">View</button>
          <button class="btn btn-sm btn-info" (click)="editItem(item)">Edit</button>
          <button class="btn btn-sm btn-danger" (click)="deleteItem(item)">Delete</button>
        </div>
      </div>
    </div>
  </div>

  <!-- Detail View -->
  <div *ngIf="isViewing && detailItem" class="detail-view">
    <div class="detail-header">
      <div class="education-icon-large">{{ getTypeIcon(detailItem.type) }}</div>
      <h2>{{ detailItem.institution }}</h2>
      <span class="type-badge" [ngClass]="getTypeClass(detailItem.type)">{{ detailItem.type }}</span>
    </div>

    <div class="detail-content">
      <div class="detail-section">
        <label>Duration</label>
        <p>{{ detailItem.duration }}</p>
      </div>

      <div class="detail-section">
        <label>Subjects</label>
        <div class="subjects-detail">
          <span class="subject-tag" *ngFor="let subject of detailItem.subjects">
            {{ subject }}
          </span>
        </div>
      </div>

      <div class="detail-section">
        <label>Type</label>
        <p>{{ detailItem.type }}</p>
      </div>
    </div>

    <div class="detail-actions">
      <button class="btn btn-info" (click)="editItem(detailItem)">Edit</button>
      <button class="btn btn-secondary" (click)="cancelAction()">Back to List</button>
    </div>
  </div>

  <!-- Create/Update Form -->
  <div *ngIf="(isCreating || isUpdating) && !loading" class="form-view">
    <div class="form-header">
      <h2>{{ isCreating ? 'Create' : 'Edit' }} {{ modelName }}</h2>
    </div>

    <form class="form">
      <div class="form-row">
        <div class="form-group full-width">
          <label for="institution">Institution *</label>
          <input 
            id="institution" 
            type="text" 
            class="form-control" 
            [(ngModel)]="formData.institution"
            name="institution"
            placeholder="e.g., University of Technology (TU Wien), Vienna - Austria"
            required>
        </div>
      </div>

      <div class="form-row">
        <div class="form-group">
          <label for="type">Type</label>
          <select 
            id="type" 
            class="form-control" 
            [(ngModel)]="formData.type"
            name="type">
            <option *ngFor="let type of typeOptions" [value]="type">{{ type }}</option>
          </select>
        </div>

        <div class="form-group">
          <label for="duration">Duration *</label>
          <input 
            id="duration" 
            type="text" 
            class="form-control" 
            [(ngModel)]="formData.duration"
            name="duration"
            placeholder="e.g., October, 2004 - June 2010"
            required>
        </div>
      </div>

      <div class="form-row">
        <div class="form-group full-width">
          <label for="subjects">Subjects (comma-separated)</label>
          <textarea 
            id="subjects" 
            class="form-control" 
            [(ngModel)]="subjectsInput"
            name="subjects"
            rows="3"
            placeholder="e.g., Computer Science, Economics, Mathematics"></textarea>
        </div>
      </div>

      <div class="form-actions">
        <button type="button" class="btn btn-primary" (click)="saveItem()">Save</button>
        <button type="button" class="btn btn-secondary" (click)="cancelAction()">Cancel</button>
      </div>
    </form>
  </div>

  <!-- Delete Confirmation -->
  <div *ngIf="isDeleting && !loading" class="delete-confirmation">
    <div class="confirmation-dialog">
      <h2>Delete {{ modelName }}</h2>
      <p>Are you sure you want to delete this education record?</p>
      <p class="item-info">{{ selectedItem?.institution }} ({{ selectedItem?.type }})</p>

      <div class="confirmation-actions">
        <button class="btn btn-danger" (click)="confirmDelete()">Delete</button>
        <button class="btn btn-secondary" (click)="cancelAction()">Cancel</button>
      </div>
    </div>
  </div>
</div>
