import { Component, signal, OnInit, AfterViewChecked, ElementRef, ViewChild } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { ProjectService } from '@app/core/services';
import { ConsultingInquiry } from '@app/core/models';
import { ProjectType } from '@app/core/models/project.model';

declare var paypal: any;

@Component({
  selector: 'app-hire',
  standalone: true,
  imports: [CommonModule, FormsModule],
  templateUrl: './hire.component.html',
  styleUrls: ['./hire.component.scss']
})
export class HireComponent implements OnInit, AfterViewChecked {
  @ViewChild('paypalButtonContainer') paypalButtonContainer!: ElementRef;

  inquiry: ConsultingInquiry = {
    name: '',
    email: '',
    type: 'Architecture',
    message: ''
  };

  engagementTypes: ProjectType[] = ['Architecture', 'DevOps', 'Migration'];
  highLoad = signal(true);

  // Payment flow state
  paymentCompleted = signal(false);
  paymentInProgress = signal(false);
  paymentError = signal<string | null>(null);
  transactionId = signal<string | null>(null);
  showPaymentStep = signal(false);
  paypalButtonRendered = false;

  // PayPal configuration
  readonly PAYPAL_ACCOUNT = 'kojo.ampia@jojoaddison.net';
  readonly INQUIRY_FEE = 1.99;
  readonly CURRENCY = 'EUR';

  constructor(private projectService: ProjectService) {}

  ngOnInit(): void {
    this.loadPayPalScript();
  }

  ngAfterViewChecked(): void {
    if (this.showPaymentStep() && this.paypalButtonContainer && !this.paypalButtonRendered) {
      this.renderPayPalButton();
    }
  }

  private loadPayPalScript(): void {
    if (document.getElementById('paypal-script')) {
      return;
    }

    const script = document.createElement('script');
    script.id = 'paypal-script';
    script.src = `https://www.paypal.com/sdk/js?client-id=sb&currency=${this.CURRENCY}`;
    script.onload = () => {
      console.log('PayPal SDK loaded');
    };
    document.body.appendChild(script);
  }

  private renderPayPalButton(): void {
    if (typeof paypal === 'undefined' || !this.paypalButtonContainer?.nativeElement) {
      return;
    }

    this.paypalButtonRendered = true;
    this.paypalButtonContainer.nativeElement.innerHTML = '';

    paypal.Buttons({
      style: {
        layout: 'vertical',
        color: 'blue',
        shape: 'rect',
        label: 'pay'
      },
      createOrder: (data: any, actions: any) => {
        return actions.order.create({
          purchase_units: [{
            description: 'Consulting Inquiry Fee',
            amount: {
              currency_code: this.CURRENCY,
              value: this.INQUIRY_FEE.toFixed(2)
            },
            payee: {
              email_address: this.PAYPAL_ACCOUNT
            }
          }]
        });
      },
      onApprove: async (data: any, actions: any) => {
        this.paymentInProgress.set(true);
        this.paymentError.set(null);

        try {
          const order = await actions.order.capture();
          this.transactionId.set(order.id);
          this.paymentCompleted.set(true);
          this.paymentInProgress.set(false);
          console.log('Payment successful:', order);
        } catch (error) {
          this.paymentError.set('Payment capture failed. Please try again.');
          this.paymentInProgress.set(false);
          console.error('Payment error:', error);
        }
      },
      onError: (err: any) => {
        this.paymentError.set('Payment failed. Please try again.');
        this.paymentInProgress.set(false);
        console.error('PayPal error:', err);
      },
      onCancel: () => {
        this.paymentError.set('Payment was cancelled.');
        this.paymentInProgress.set(false);
      }
    }).render(this.paypalButtonContainer.nativeElement);
  }

  isFormValid(): boolean {
    return !!(this.inquiry.name?.trim() && 
              this.inquiry.email?.trim() && 
              this.inquiry.type && 
              this.inquiry.message?.trim());
  }

  proceedToPayment(): void {
    if (!this.isFormValid()) {
      this.paymentError.set('Please fill in all required fields before proceeding to payment.');
      return;
    }
    this.paymentError.set(null);
    this.showPaymentStep.set(true);
    this.paypalButtonRendered = false;
  }

  cancelPayment(): void {
    this.showPaymentStep.set(false);
    this.paymentError.set(null);
    this.paypalButtonRendered = false;
  }

  selectEngagementType(type: string): void {
    this.inquiry.type = type as ProjectType;
  }

  submitInquiry(): void {
    if (!this.paymentCompleted()) {
      this.paymentError.set('Payment is required before submitting your inquiry.');
      return;
    }

    const newProject = {
      name: `${this.inquiry.type} Strategy`,
      client: this.inquiry.name,
      type: this.inquiry.type,
      description: this.inquiry.message,
      stack: ['Planning', 'TBD'],
      status: 'Pending' as const,
      architecture: 'Under Review',
      paymentTransactionId: this.transactionId()
    };

    this.projectService.addProject(newProject);

    alert(`Contract Initialized for ${this.inquiry.name}. Deployment pipeline started. Transaction ID: ${this.transactionId()}`);

    // Reset form and payment state
    this.inquiry = {
      name: '',
      email: '',
      type: 'Architecture',
      message: ''
    };
    this.paymentCompleted.set(false);
    this.transactionId.set(null);
    this.showPaymentStep.set(false);
    this.paypalButtonRendered = false;
  }
}
<div class="space-y-8 animate-in fade-in slide-in-from-bottom-4 duration-500">
  <div class="flex justify-between items-center">
    <h2 class="text-xl font-bold text-slate-100 flex items-center gap-3">
      <i class="fas fa-briefcase text-cyan-500"></i>
      Consulting Engagement
    </h2>
    <div class="flex items-center gap-2">
      <span class="h-2 w-2 rounded-full" [ngClass]="{'bg-green-500 shadow-[0_0_8px_rgba(34,197,94,0.6)]': highLoad(), 'bg-amber-500': !highLoad()}"></span>
      <span class="text-xs font-mono text-slate-400">{{ highLoad() ? 'High Load - Waitlist' : 'Available for engagement' }}</span>
    </div>
  </div>

  <!-- Payment Modal Overlay -->
  @if (showPaymentStep()) {
    <div class="fixed inset-0 bg-black/70 backdrop-blur-sm z-50 flex items-center justify-center animate-in fade-in duration-300">
      <div class="bg-slate-900 border border-slate-700 rounded-xl p-6 max-w-md w-full mx-4 shadow-2xl animate-in zoom-in-95 duration-300">
        <div class="flex items-center justify-between mb-4">
          <h3 class="text-lg font-bold text-slate-100 flex items-center gap-2">
            <i class="fab fa-paypal text-blue-500"></i> Payment Required
          </h3>
          <button (click)="cancelPayment()" class="text-slate-400 hover:text-slate-200 transition-colors">
            <i class="fas fa-times"></i>
          </button>
        </div>

        <!-- Payment Info -->
        <div class="bg-slate-950 border border-slate-800 rounded-lg p-4 mb-4">
          <div class="flex justify-between items-center mb-2">
            <span class="text-sm text-slate-400">Inquiry Fee</span>
            <span class="text-xl font-bold text-cyan-400">€{{ INQUIRY_FEE }}</span>
          </div>
          <p class="text-xs text-slate-500">One-time fee to process your consulting request. This helps ensure quality engagements.</p>
        </div>

        <!-- Summary of inquiry -->
        <div class="bg-slate-950/50 border border-slate-800 rounded-lg p-3 mb-4 text-xs">
          <div class="text-slate-500 uppercase font-bold mb-2">Inquiry Summary</div>
          <div class="space-y-1 text-slate-400">
            <div><span class="text-slate-500">Client:</span> {{ inquiry.name }}</div>
            <div><span class="text-slate-500">Email:</span> {{ inquiry.email }}</div>
            <div><span class="text-slate-500">Type:</span> {{ inquiry.type }}</div>
          </div>
        </div>

        <!-- Payment Status -->
        @if (paymentInProgress()) {
          <div class="flex items-center justify-center gap-2 py-4 text-cyan-400">
            <i class="fas fa-spinner animate-spin"></i>
            <span>Processing payment...</span>
          </div>
        }

        @if (paymentCompleted()) {
          <div class="bg-green-900/30 border border-green-700 rounded-lg p-4 mb-4 animate-in fade-in duration-300">
            <div class="flex items-center gap-2 text-green-400 mb-2">
              <i class="fas fa-check-circle"></i>
              <span class="font-bold">Payment Successful!</span>
            </div>
            <p class="text-xs text-green-500">Transaction ID: {{ transactionId() }}</p>
          </div>
          <button (click)="submitInquiry()" class="w-full px-4 py-3 rounded bg-cyan-600 hover:bg-cyan-700 text-white border border-cyan-500 transition-all text-sm font-bold flex items-center justify-center gap-2 group">
            <i class="fas fa-paper-plane"></i> Submit Inquiry Now
            <span class="group-hover:translate-x-1 transition-transform">→</span>
          </button>
        } @else {
          <!-- PayPal Button Container -->
          <div #paypalButtonContainer class="paypal-button-container min-h-[150px]"></div>
        }

        @if (paymentError()) {
          <div class="mt-4 bg-red-900/30 border border-red-700 rounded-lg p-3 text-red-400 text-sm flex items-center gap-2">
            <i class="fas fa-exclamation-circle"></i>
            {{ paymentError() }}
          </div>
        }

        <button (click)="cancelPayment()" class="w-full mt-4 px-4 py-2 rounded bg-slate-800 hover:bg-slate-700 text-slate-300 border border-slate-700 transition-all text-sm">
          Cancel
        </button>
      </div>
    </div>
  }

  <!-- Contract Form -->
  <div class="bg-slate-900 border border-slate-800 rounded-lg p-6 space-y-6">
    <div class="flex items-center justify-between">
      <h3 class="text-lg font-bold text-slate-100">New Engagement</h3>
      <div class="flex items-center gap-2 text-xs text-slate-500">
        <i class="fab fa-paypal text-blue-500"></i>
        <span>€{{ INQUIRY_FEE }} inquiry fee applies</span>
      </div>
    </div>

    <div class="grid grid-cols-1 md:grid-cols-2 gap-4">
      <div>
        <label class="text-xs uppercase text-slate-500 font-bold tracking-wider block mb-2">Client Name <span class="text-red-500">*</span></label>
        <input type="text" [(ngModel)]="inquiry.name" placeholder="Your Organization" class="w-full px-3 py-2 rounded bg-slate-950 border border-slate-800 text-slate-200 focus:border-cyan-500 focus:outline-none transition-colors text-sm placeholder-slate-600" />
      </div>
      <div>
        <label class="text-xs uppercase text-slate-500 font-bold tracking-wider block mb-2">Email <span class="text-red-500">*</span></label>
        <input type="email" [(ngModel)]="inquiry.email" placeholder="contact&#64;organization.com" class="w-full px-3 py-2 rounded bg-slate-950 border border-slate-800 text-slate-200 focus:border-cyan-500 focus:outline-none transition-colors text-sm placeholder-slate-600" />
      </div>
    </div>

    <div>
      <label class="text-xs uppercase text-slate-500 font-bold tracking-wider block mb-3">Engagement Type <span class="text-red-500">*</span></label>
      <div class="grid grid-cols-2 md:grid-cols-3 gap-2">
        <button (click)="selectEngagementType('Architecture')" [ngClass]="{'bg-cyan-600 border-cyan-500 text-white': inquiry.type === 'Architecture', 'bg-slate-800 border-slate-700 text-slate-200 hover:bg-slate-700 hover:border-slate-600': inquiry.type !== 'Architecture'}" class="px-3 py-2 rounded border transition-all text-xs font-bold">ARCHITECTURE</button>
        <button (click)="selectEngagementType('Microservices')" [ngClass]="{'bg-cyan-600 border-cyan-500 text-white': inquiry.type === 'Microservices', 'bg-slate-800 border-slate-700 text-slate-200 hover:bg-slate-700 hover:border-slate-600': inquiry.type !== 'Microservices'}" class="px-3 py-2 rounded border transition-all text-xs font-bold">MICROSERVICES</button>
        <button (click)="selectEngagementType('DevOps')" [ngClass]="{'bg-cyan-600 border-cyan-500 text-white': inquiry.type === 'DevOps', 'bg-slate-800 border-slate-700 text-slate-200 hover:bg-slate-700 hover:border-slate-600': inquiry.type !== 'DevOps'}" class="px-3 py-2 rounded border transition-all text-xs font-bold">DEVOPS</button>
        <button (click)="selectEngagementType('Mentoring')" [ngClass]="{'bg-cyan-600 border-cyan-500 text-white': inquiry.type === 'Mentoring', 'bg-slate-800 border-slate-700 text-slate-200 hover:bg-slate-700 hover:border-slate-600': inquiry.type !== 'Mentoring'}" class="px-3 py-2 rounded border transition-all text-xs font-bold">MENTORING</button>
        <button (click)="selectEngagementType('Code Review')" [ngClass]="{'bg-cyan-600 border-cyan-500 text-white': inquiry.type === 'Code Review', 'bg-slate-800 border-slate-700 text-slate-200 hover:bg-slate-700 hover:border-slate-600': inquiry.type !== 'Code Review'}" class="px-3 py-2 rounded border transition-all text-xs font-bold">CODE REVIEW</button>
        <button (click)="selectEngagementType('Migration')" [ngClass]="{'bg-cyan-600 border-cyan-500 text-white': inquiry.type === 'Migration', 'bg-slate-800 border-slate-700 text-slate-200 hover:bg-slate-700 hover:border-slate-600': inquiry.type !== 'Migration'}" class="px-3 py-2 rounded border transition-all text-xs font-bold">MIGRATION</button>
      </div>
    </div>

    <div>
      <label class="text-xs uppercase text-slate-500 font-bold tracking-wider block mb-2">Project Scope <span class="text-red-500">*</span></label>
      <textarea [(ngModel)]="inquiry.message" placeholder="Describe your project needs and timeline..." rows="4" class="w-full px-3 py-2 rounded bg-slate-950 border border-slate-800 text-slate-200 focus:border-cyan-500 focus:outline-none transition-colors text-sm placeholder-slate-600 resize-none"></textarea>
    </div>

    @if (paymentError() && !showPaymentStep()) {
      <div class="bg-red-900/30 border border-red-700 rounded-lg p-3 text-red-400 text-sm flex items-center gap-2">
        <i class="fas fa-exclamation-circle"></i>
        {{ paymentError() }}
      </div>
    }

    <button (click)="proceedToPayment()" [disabled]="!isFormValid()" [ngClass]="{'opacity-50 cursor-not-allowed': !isFormValid()}" class="w-full px-4 py-3 rounded bg-cyan-600 hover:bg-cyan-700 text-white border border-cyan-500 transition-all text-sm font-bold flex items-center justify-center gap-2 group">
      <i class="fab fa-paypal"></i> Proceed to Payment (€{{ INQUIRY_FEE }})
      <span class="group-hover:translate-x-1 transition-transform">→</span>
