import { Injectable } from '@angular/core';
import { BehaviorSubject, Observable } from 'rxjs';
import { UserProfile } from '@app/core/models/profile.model';

@Injectable({
  providedIn: 'root'
})
export class ProfileService {
  delete(id: boolean) {
      throw new Error('Method not implemented.');
  }
  update(id: boolean, profile: UserProfile) {
      throw new Error('Method not implemented.');
  }
  create(profile: UserProfile) {
      throw new Error('Method not implemented.');
  }
  getAll() {
      throw new Error('Method not implemented.');
  }
  private profile$ = new BehaviorSubject<UserProfile>({
    id: 'jkaa-001',
    name: 'John Kojo Ampia-Addison',
    title: 'Senior Software Architect & DevOps Engineer',
    email: 'kojo.ampia@jojoaddison.net',
    phone: '+43 676 922 1796',
    location: 'Vienna, Austria',
    avatar: 'assets/kojo-ampia-addison.jpeg'
  });

  constructor() {}

  getProfile(): Observable<UserProfile> {
    return this.profile$.asObservable();
  }

  updateProfile(profile: Partial<UserProfile>): void {
    const current = this.profile$.value;
    this.profile$.next({ ...current, ...profile });
  }
}
