import { Component, Inject } from '@angular/core';
import { MAT_SNACK_BAR_DATA, MatSnackBarRef } from '@angular/material/snack-bar';

export interface ToastData {
  closable?: boolean;
  message?: string;
  translationMessageKey?: string;
  type: 'info' | 'error' | 'success';
}

@Component({
  selector: 'eco-toast',
  templateUrl: './toast.component.html',
  styleUrls: ['./toast.component.scss'],
})
export class ToastComponent {
  constructor(
    @Inject(MAT_SNACK_BAR_DATA) public data: ToastData,
    @Inject(MatSnackBarRef) private readonly snackBarRef: MatSnackBarRef<ToastComponent>
  ) {}

  public close(): void {
    this.snackBarRef.dismiss();
  }

  public getBoolean(value: boolean | undefined): boolean {
    return value ?? true;
  }
}
<div *ngIf="!!data">
  <div *ngIf="getBoolean(data.closable)" class="flex justify-end m0 p0">
    <button mat-icon-button (click)="close()">
      <mat-icon>close</mat-icon>
    </button>
  </div>
  <div class="toast-content">
    <ng-container *ngIf="data.message">{{data.message}}</ng-container>
    <ng-container *ngIf="data.translationMessageKey">{{data.translationMessageKey | translate}}</ng-container>
  </div>
</div>
