import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { BehaviorSubject, Observable, tap } from 'rxjs';
import { environment } from '../../../environments/environment';

export interface UserLoginDTO {
  id?: string;
  username: string;
  email?: string;
  password: string;
  authorities?: Authority[];
}

export interface Authority {
  id?: string;
  name: string;
}

export interface AuthResponse {
  token: string;
  user: UserLoginDTO;
}

@Injectable({
  providedIn: 'root'
})
export class AuthService {
  private readonly API_URL = `${environment.apiUrl}/api/v1/auth`;
  private readonly currentUserSubject = new BehaviorSubject<UserLoginDTO | null>(this.getUserFromStorage());
  public currentUser$ = this.currentUserSubject.asObservable();

  constructor(private readonly http: HttpClient) {}

  /**
   * Login with username and password
   */
  login(username: string, password: string): Observable<AuthResponse> {
    const loginRequest: UserLoginDTO = { username, password };

    return this.http.post<AuthResponse>(`${this.API_URL}/login`, loginRequest).pipe(
      tap((response) => {
        // Store token
        if (response.token) {
          localStorage.setItem('auth_token', response.token);
        }
        // Store user
        if (response.user) {
          localStorage.setItem('current_user', JSON.stringify(response.user));
          this.currentUserSubject.next(response.user);
        }
      })
    );
  }

  /**
   * Validate token with the server
   */
  validateToken(): Observable<{ valid: boolean }> {
    const token = this.getToken();
    if (!token) {
      return new Observable((observer) => {
        observer.next({ valid: false });
        observer.complete();
      });
    }

    return this.http.post<{ valid: boolean }>(`${this.API_URL}/validate`, { token });
  }

  /**
   * Logout user
   */
  logout(): void {
    localStorage.removeItem('auth_token');
    localStorage.removeItem('current_user');
    this.currentUserSubject.next(null);
  }

  /**
   * Check if user is logged in
   */
  isLoggedIn(): boolean {
    return !!this.getToken();
  }

  /**
   * Get current token
   */
  getToken(): string | null {
    return localStorage.getItem('auth_token');
  }

  /**
   * Get current user
   */
  getCurrentUser(): UserLoginDTO | null {
    return this.currentUserSubject.value;
  }

  /**
   * Get user from storage
   */
  private getUserFromStorage(): UserLoginDTO | null {
    const user = localStorage.getItem('current_user');
    return user ? JSON.parse(user) : null;
  }

  /**
   * Check if current user has specific role
   */
  hasRole(role: string): boolean {
    const user = this.getCurrentUser();
    return !!user?.authorities?.some((auth) => auth.name === role || auth.name === `ROLE_${role}`);
  }
}
