export class DocumentationDashboardComponent {
  constructor(documentationService) {
    this.documentationService = documentationService;
    this.modelName = 'Documentation';
    this.items = [];
    this.filteredItems = [];
    this.selectedItem = null;
    this.detailItem = null;
    this.isCreating = false;
    this.isUpdating = false;
    this.isDeleting = false;
    this.isViewing = false;
    this.currentAction = 'list';
    this.loading = false;
    this.error = null;
    this.selectedType = 'All';
    this.typeOptions = ['ADR', 'RFC', 'Manual', 'Policy'];
    this.formData = {
      id: '',
      title: '',
      type: 'ADR',
      tags: [],
      lastUpdated: '',
      content: ''
    };
    this.tagsInput = '';
  }
  ngOnInit() {
    this.loadItems();
  }
  loadItems() {
    this.loading = true;
    this.documentationService.getDocuments().subscribe({
      next: items => {
        this.items = items;
        this.filterByType();
        this.loading = false;
      },
      error: err => {
        this.error = 'Failed to load documentation: ' + err.message;
        this.loading = false;
      }
    });
  }
  filterByType() {
    if (this.selectedType === 'All') {
      this.filteredItems = [...this.items];
    } else {
      this.filteredItems = this.items.filter(d => d.type === this.selectedType);
    }
  }
  onTypeFilterChange() {
    this.filterByType();
  }
  selectItem(item) {
    this.selectedItem = item;
  }
  viewItem(item) {
    this.currentAction = 'view';
    this.isViewing = true;
    this.detailItem = item;
    this.error = null;
  }
  createItem() {
    this.currentAction = 'create';
    this.isCreating = true;
    this.selectedItem = null;
    this.tagsInput = '';
