import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { RouterModule } from '@angular/router';
import { Documentation, DocumentType } from '@app/core/models/documentation.model';
import { DocumentationService } from '@app/core/services/documentation.service';

@Component({
  selector: 'app-documentation-dashboard',
  standalone: true,
  imports: [CommonModule, FormsModule, RouterModule],
  templateUrl: './documentation-dashboard.component.html',
  styleUrls: ['./documentation-dashboard.component.scss']
})
export class DocumentationDashboardComponent implements OnInit {
  modelName = 'Documentation';
  items: Documentation[] = [];
  filteredItems: Documentation[] = [];
  selectedItem: Documentation | null = null;
  detailItem: Documentation | null = null;
  isCreating = false;
  isUpdating = false;
  isDeleting = false;
  isViewing = false;
  currentAction: 'list' | 'create' | 'update' | 'delete' | 'view' = 'list';
  loading = false;
  error: string | null = null;
  selectedType: DocumentType | 'All' = 'All';

  typeOptions: DocumentType[] = ['ADR', 'RFC', 'Manual', 'Policy'];

  formData: Partial<Documentation> = {
    id: '',
    title: '',
    type: 'ADR',
    tags: [],
    lastUpdated: '',
    content: ''
  };

  tagsInput = '';

  constructor(private documentationService: DocumentationService) {}

  ngOnInit(): void {
    this.loadItems();
  }

  loadItems(): void {
    this.loading = true;
    this.documentationService.getDocuments().subscribe({
      next: (items: Documentation[]) => {
        this.items = items;
        this.filterByType();
        this.loading = false;
      },
      error: (err: any) => {
        this.error = 'Failed to load documentation: ' + err.message;
        this.loading = false;
      }
    });
  }

  filterByType(): void {
    if (this.selectedType === 'All') {
      this.filteredItems = [...this.items];
    } else {
      this.filteredItems = this.items.filter(d => d.type === this.selectedType);
    }
  }

  onTypeFilterChange(): void {
    this.filterByType();
  }

  selectItem(item: Documentation): void {
    this.selectedItem = item;
  }

  viewItem(item: Documentation): void {
    this.currentAction = 'view';
    this.isViewing = true;
    this.detailItem = item;
    this.error = null;
  }

  createItem(): void {
    this.currentAction = 'create';
    this.isCreating = true;
    this.selectedItem = null;
    this.tagsInput = '';
    this.formData = {
      id: '',
      title: '',
      type: 'ADR',
      tags: [],
      lastUpdated: new Date().toISOString().split('T')[0],
      content: ''
    };
    this.error = null;
  }

  editItem(item: Documentation): void {
    this.currentAction = 'update';
    this.isUpdating = true;
    this.selectedItem = item;
    this.formData = { ...item, tags: [...item.tags] };
    this.tagsInput = item.tags.join(', ');
    this.error = null;
  }

  deleteItem(item: Documentation): void {
    this.currentAction = 'delete';
    this.isDeleting = true;
    this.selectedItem = item;
    this.error = null;
  }

  saveItem(): void {
    this.loading = true;

    // Parse tags from comma-separated input
    this.formData.tags = this.tagsInput.split(',').map(t => t.trim()).filter(t => t);
    this.formData.lastUpdated = new Date().toISOString().split('T')[0];

    const doc = { ...this.selectedItem, ...this.formData } as Documentation;

    if (this.isCreating) {
      this.documentationService.createDocument(doc);
      this.loadItems();
      this.cancelAction();
    } else if (this.isUpdating && doc.id) {
      this.documentationService.updateDocument(doc.id, doc);
      this.loadItems();
      this.cancelAction();
    }
    this.loading = false;
  }

  confirmDelete(): void {
    if (this.selectedItem?.id) {
      this.documentationService.deleteDocument(this.selectedItem.id);
      this.loadItems();
    }
    this.cancelAction();
  }

  cancelAction(): void {
    this.currentAction = 'list';
    this.isCreating = false;
    this.isUpdating = false;
    this.isDeleting = false;
    this.isViewing = false;
    this.selectedItem = null;
    this.detailItem = null;
    this.error = null;
    this.formData = {};
    this.tagsInput = '';
  }

  goBack(): void {
    this.cancelAction();
  }

  getTypeClass(type: string): string {
    const typeClasses: { [key: string]: string } = {
      'ADR': 'type-adr',
      'RFC': 'type-rfc',
      'Manual': 'type-manual',
      'Policy': 'type-policy'
    };
    return typeClasses[type] || '';
  }

  getTypeIcon(type: string): string {
    const typeIcons: { [key: string]: string } = {
      'ADR': 'üìã',
      'RFC': 'üìù',
      'Manual': 'üìñ',
      'Policy': 'üìú'
    };
    return typeIcons[type] || 'üìÑ';
  }
}
<div class="model-dashboard">
  <!-- Back to Admin Button -->
  <div class="nav-header">
    <a routerLink="/management" class="btn btn-back">
      ‚Üê Back to Admin Dashboard
    </a>
  </div>

  <!-- Header -->
  <div class="dashboard-header">
    <h1>üìÑ {{ modelName }} Management</h1>
    <div class="header-actions">
      <select class="form-control type-filter" [(ngModel)]="selectedType" (change)="onTypeFilterChange()">
        <option value="All">All Types</option>
        <option *ngFor="let type of typeOptions" [value]="type">{{ type }}</option>
      </select>
      <button 
        class="btn btn-primary" 
        (click)="createItem()"
        [disabled]="loading">
        + Add New {{ modelName }}
      </button>
    </div>
  </div>

  <!-- Error Message -->
  <div *ngIf="error" class="alert alert-error">
    {{ error }}
    <button class="close-btn" (click)="error = null">√ó</button>
  </div>

  <!-- Loading Indicator -->
  <div *ngIf="loading" class="loading">
    <div class="spinner"></div>
    Loading...
  </div>

  <!-- List View -->
  <div *ngIf="currentAction === 'list' && !loading" class="list-view">
    <div *ngIf="filteredItems.length === 0" class="empty-state">
      <p>No {{ modelName | lowercase }} found</p>
      <button class="btn btn-primary" (click)="createItem()">Create First {{ modelName }}</button>
    </div>

    <div class="docs-grid" *ngIf="filteredItems.length > 0">
      <div class="doc-card" *ngFor="let item of filteredItems" 
           [ngClass]="getTypeClass(item.type)"
           [class.selected]="selectedItem?.id === item.id">
        <div class="card-header">
          <span class="type-badge" [ngClass]="getTypeClass(item.type)">
            {{ getTypeIcon(item.type) }} {{ item.type }}
          </span>
          <span class="doc-id">{{ item.id }}</span>
        </div>
        <div class="card-body">
          <h3>{{ item.title }}</h3>
          <div class="doc-tags">
            <span class="tag" *ngFor="let tag of item.tags | slice:0:3">{{ tag }}</span>
            <span class="tag more" *ngIf="item.tags.length > 3">+{{ item.tags.length - 3 }}</span>
          </div>
          <div class="last-updated">
            <span class="label">Last Updated:</span>
            <span class="date">{{ item.lastUpdated }}</span>
          </div>
        </div>
        <div class="card-actions">
          <button class="btn btn-sm btn-view" (click)="viewItem(item)">View</button>
          <button class="btn btn-sm btn-info" (click)="editItem(item)">Edit</button>
          <button class="btn btn-sm btn-danger" (click)="deleteItem(item)">Delete</button>
        </div>
      </div>
    </div>
  </div>

  <!-- Detail View -->
  <div *ngIf="isViewing && detailItem" class="detail-view">
    <div class="detail-header">
      <div class="header-top">
        <span class="type-badge" [ngClass]="getTypeClass(detailItem.type)">
          {{ getTypeIcon(detailItem.type) }} {{ detailItem.type }}
        </span>
        <span class="doc-id">{{ detailItem.id }}</span>
      </div>
      <h2>{{ detailItem.title }}</h2>
      <div class="header-meta">
        <span class="last-updated">Last Updated: {{ detailItem.lastUpdated }}</span>
      </div>
    </div>

    <div class="detail-content">
      <div class="detail-section">
        <label>Tags</label>
        <div class="doc-tags">
          <span class="tag" *ngFor="let tag of detailItem.tags">{{ tag }}</span>
        </div>
      </div>

      <div class="detail-section content-section">
        <label>Content</label>
        <div class="doc-content">
          <pre>{{ detailItem.content }}</pre>
        </div>
      </div>
    </div>

    <div class="detail-actions">
      <button class="btn btn-info" (click)="editItem(detailItem)">Edit</button>
      <button class="btn btn-secondary" (click)="cancelAction()">Back to List</button>
    </div>
  </div>

  <!-- Create/Update Form -->
  <div *ngIf="(isCreating || isUpdating) && !loading" class="form-view">
    <div class="form-header">
      <h2>{{ isCreating ? 'Create' : 'Edit' }} {{ modelName }}</h2>
    </div>

    <form class="form">
      <div class="form-row">
        <div class="form-group">
          <label for="id">Document ID *</label>
          <input 
            id="id" 
            type="text" 
            class="form-control" 
            [(ngModel)]="formData.id"
            name="id"
            placeholder="e.g., ADR-2024-001"
            [disabled]="isUpdating"
            required>
        </div>

        <div class="form-group">
          <label for="type">Document Type</label>
          <select 
            id="type" 
            class="form-control" 
            [(ngModel)]="formData.type"
            name="type">
            <option *ngFor="let type of typeOptions" [value]="type">{{ type }}</option>
          </select>
        </div>
      </div>

      <div class="form-group">
        <label for="title">Title *</label>
        <input 
          id="title" 
          type="text" 
          class="form-control" 
          [(ngModel)]="formData.title"
          name="title"
          placeholder="e.g., Event-Driven Architecture Strategy"
          required>
      </div>

      <div class="form-group">
        <label for="tags">Tags (comma-separated)</label>
        <input 
          id="tags" 
          type="text" 
          class="form-control" 
          [(ngModel)]="tagsInput"
          name="tags"
          placeholder="e.g., Kafka, Microservices, Architecture">
      </div>

      <div class="form-group">
        <label for="content">Content (Markdown supported)</label>
        <textarea 
          id="content" 
          class="form-control content-editor" 
          [(ngModel)]="formData.content"
          name="content"
          rows="15"
          placeholder="# Document Title

## Context
Describe the context...

## Decision
Describe the decision...

## Consequences
List consequences..."></textarea>
      </div>

      <div class="form-actions">
        <button type="button" class="btn btn-primary" (click)="saveItem()">Save</button>
        <button type="button" class="btn btn-secondary" (click)="cancelAction()">Cancel</button>
      </div>
    </form>
  </div>

  <!-- Delete Confirmation -->
  <div *ngIf="isDeleting && !loading" class="delete-confirmation">
    <div class="confirmation-dialog">
      <h2>Delete {{ modelName }}</h2>
      <p>Are you sure you want to delete this document?</p>
      <p class="item-info">{{ selectedItem?.id }} - {{ selectedItem?.title }}</p>

      <div class="confirmation-actions">
        <button class="btn btn-danger" (click)="confirmDelete()">Delete</button>
        <button class="btn btn-secondary" (click)="cancelAction()">Cancel</button>
      </div>
    </div>
  </div>
</div>
