import { Injectable } from '@angular/core';
import {
  HttpRequest,
  HttpHandler,
  HttpEvent,
  HttpInterceptor
} from '@angular/common/http';
import { Observable } from 'rxjs';

/**
 * HTTP Interceptor that adds X-Trace-Id header to all HTTP requests
 * for distributed tracing and request tracking across services
 */
@Injectable()
export class TraceIdInterceptor implements HttpInterceptor {
  /**
   * Generate a unique trace ID (UUID v4)
   */
  private generateTraceId(): string {
    return 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, (c) => {
      const r = (Math.random() * 16) | 0;
      const v = c === 'x' ? r : (r & 0x3) | 0x8;
      return v.toString(16);
    });
  }

  intercept(request: HttpRequest<unknown>, next: HttpHandler): Observable<HttpEvent<unknown>> {
    // Generate a unique trace ID for this request
    const traceId = this.generateTraceId();

    // Clone the request and add the X-Trace-Id header
    const clonedRequest = request.clone({
      setHeaders: {
        'X-Trace-Id': traceId
      }
    });

    // Log the trace ID for debugging (optional)
    if (request.method !== 'GET') {
      console.debug(`[${traceId}] ${request.method} ${request.url}`);
    }

    // Forward the modified request
    return next.handle(clonedRequest);
  }
}
