import { Component, Input, Output, EventEmitter, OnInit, signal } from '@angular/core';
import { CommonModule } from '@angular/common';
import { SidebarComponent } from './sidebar.component';
import { UserProfile, UserService } from '@app/core/services';

@Component({
  selector: 'app-header',
  standalone: true,
  imports: [CommonModule, SidebarComponent],
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.scss']
})
export class HeaderComponent implements OnInit {
  sidebar!: SidebarComponent;

  profile = signal<UserProfile | null>(null);

  constructor(private readonly userService: UserService) {}

  ngOnInit(): void {
    this.userService.getProfile().subscribe(profile => {
      this.profile.set(profile);
    });
  }  
  onHireClick(): void {
    this.sidebar.onNavClick('Hire');
  }
}
<header class="bg-slate-900/80 backdrop-blur border-b border-slate-800 sticky top-0 z-10 px-6 py-4 flex justify-between items-center">
  <div>
    <h1 class="text-xl font-bold text-white">{{ profile()?.name }}</h1>
    <p class="text-sm text-cyan-500 font-mono">{{ profile()?.title }}</p>
  </div>
  <div class="flex gap-4">
    <a class="px-4 py-2 bg-slate-800 hover:bg-slate-700 border border-slate-700 rounded text-sm transition-colors text-slate-300"
     href="assets/CV_John_Kojo_AMPIA-ADDISON_2026_V1.pdf" target="_blank">
      <i class="fas fa-download mr-2"></i> Export CV
    </a>
    <button 
      class="px-4 py-2 bg-blue-600 hover:bg-blue-500 text-white rounded text-sm shadow-lg shadow-blue-500/20 transition-all font-medium"
      (click)="onHireClick()">
      Hire Consultant
    </button>
  </div>
</header>
