import { Component, CUSTOM_ELEMENTS_SCHEMA, Inject } from '@angular/core';
import { MAT_SNACK_BAR_DATA, MatSnackBarRef } from '@angular/material/snack-bar';

export interface ToastData {
  closable?: boolean;
  message?: string;
  translationMessageKey?: string;
  type: 'info' | 'error' | 'success';
}

@Component({
  selector: 'eco-toast',
  templateUrl: './toast.component.html',
  styleUrls: ['./toast.component.scss'],
  schemas: [CUSTOM_ELEMENTS_SCHEMA],
})
export class ToastComponent {
  constructor(
    @Inject(MAT_SNACK_BAR_DATA) public data: ToastData,
    @Inject(MatSnackBarRef) private readonly snackBarRef: MatSnackBarRef<ToastComponent>
  ) {}

  public close(): void {
    this.snackBarRef.dismiss();
  }

  public getBoolean(value: boolean | undefined): boolean {
    return value ?? true;
  }
}
