import { Injectable } from '@angular/core';
import { BehaviorSubject, Observable } from 'rxjs';
import { ConsultingInquiry } from '../models/inquiry.model';

export interface Inquiry extends ConsultingInquiry {
  id?: string;
  status: 'New' | 'Contacted' | 'In Progress' | 'Closed';
  createdAt: string;
}

@Injectable({
  providedIn: 'root'
})
export class InquiryService {
  private inquiries$ = new BehaviorSubject<Inquiry[]>([
    {
      id: 'inq-1',
      name: 'John Smith',
      email: 'john.smith@techcorp.com',
      type: 'Microservices',
      message: 'Looking for help with microservices architecture design for our e-commerce platform.',
      status: 'New',
      createdAt: '2025-12-01'
    },
    {
      id: 'inq-2',
      name: 'Sarah Johnson',
      email: 'sarah.j@startup.io',
      type: 'DevOps',
      message: 'Need consulting on CI/CD pipeline setup and Kubernetes deployment.',
      status: 'Contacted',
      createdAt: '2025-11-15'
    },
    {
      id: 'inq-3',
      name: 'Michael Chen',
      email: 'm.chen@enterprise.com',
      type: 'Migration',
      message: 'Planning to migrate legacy Java application to cloud-native architecture.',
      status: 'In Progress',
      createdAt: '2025-10-20'
    }
  ]);

  constructor() {}

  getInquiries(): Observable<Inquiry[]> {
    return this.inquiries$.asObservable();
  }

  getInquiryById(id: string): Observable<Inquiry | undefined> {
    return new Observable(observer => {
      const inquiry = this.inquiries$.value.find(i => i.id === id);
      observer.next(inquiry);
      observer.complete();
    });
  }

  addInquiry(inquiry: Inquiry): void {
    const current = this.inquiries$.value;
    this.inquiries$.next([...current, { ...inquiry, id: `inq-${Date.now()}`, createdAt: new Date().toISOString().split('T')[0] }]);
  }

  updateInquiry(id: string, inquiry: Partial<Inquiry>): void {
    const current = this.inquiries$.value;
    const updated = current.map(i => i.id === id ? { ...i, ...inquiry } : i);
    this.inquiries$.next(updated);
  }

  deleteInquiry(id: string): void {
    const current = this.inquiries$.value;
    this.inquiries$.next(current.filter(i => i.id !== id));
  }
}
