import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { RouterModule } from '@angular/router';
import { Experience, ExperienceStatus } from '@app/core/models/experience.model';
import { Metric } from '@app/core/models/metric.model';
import { ExperienceService } from '@app/core/services/experience.service';

@Component({
  selector: 'app-experience-dashboard',
  standalone: true,
  imports: [CommonModule, FormsModule, RouterModule],
  templateUrl: './experience-dashboard.component.html',
  styleUrls: ['./experience-dashboard.component.scss']
})
export class ExperienceDashboardComponent implements OnInit {
  modelName = 'Experience';
  items: Experience[] = [];
  selectedItem: Experience | null = null;
  detailItem: Experience | null = null;
  isCreating = false;
  isUpdating = false;
  isDeleting = false;
  isViewing = false;
  currentAction: 'list' | 'create' | 'update' | 'delete' | 'view' = 'list';
  loading = false;
  error: string | null = null;

  statusOptions: ExperienceStatus[] = ['Active', 'Stable', 'Deprecated', 'Migrated'];

  formData: Partial<Experience> = {
    company: '',
    role: '',
    period: '',
    status: 'Active',
    description: '',
    stack: [],
    metrics: []
  };

  stackInput = '';
  newMetric: Metric = { label: '', value: '', trend: 'stable' };

  constructor(private experienceService: ExperienceService) {}

  ngOnInit(): void {
    this.loadItems();
  }

  loadItems(): void {
    this.loading = true;
    this.experienceService.getExperiences().subscribe({
      next: (items: Experience[]) => {
        this.items = items;
        this.loading = false;
      },
      error: (err: any) => {
        this.error = 'Failed to load experiences: ' + err.message;
        this.loading = false;
      }
    });
  }

  selectItem(item: Experience): void {
    this.selectedItem = item;
  }

  viewItem(item: Experience): void {
    this.currentAction = 'view';
    this.isViewing = true;
    this.detailItem = item;
    this.error = null;
  }

  createItem(): void {
    this.currentAction = 'create';
    this.isCreating = true;
    this.selectedItem = null;
    this.stackInput = '';
    this.formData = {
      company: '',
      role: '',
      period: '',
      status: 'Active',
      description: '',
      stack: [],
      metrics: []
    };
    this.error = null;
  }

  editItem(item: Experience): void {
    this.currentAction = 'update';
    this.isUpdating = true;
    this.selectedItem = item;
    this.formData = { ...item, stack: [...item.stack], metrics: [...item.metrics] };
    this.stackInput = item.stack.join(', ');
    this.error = null;
  }

  deleteItem(item: Experience): void {
    this.currentAction = 'delete';
    this.isDeleting = true;
    this.selectedItem = item;
    this.error = null;
  }

  addMetric(): void {
    if (this.newMetric.label && this.newMetric.value) {
      if (!this.formData.metrics) {
        this.formData.metrics = [];
      }
      this.formData.metrics.push({ ...this.newMetric });
      this.newMetric = { label: '', value: '', trend: 'stable' };
    }
  }

  removeMetric(index: number): void {
    if (this.formData.metrics) {
      this.formData.metrics.splice(index, 1);
    }
  }

  saveItem(): void {
    this.loading = true;

    // Parse stack from comma-separated input
    this.formData.stack = this.stackInput.split(',').map(s => s.trim()).filter(s => s);

    const experience = { ...this.selectedItem, ...this.formData } as Experience;

    if (this.isCreating) {
      this.experienceService.createExperience(experience);
      this.loadItems();
      this.cancelAction();
    } else if (this.isUpdating && experience.id) {
      this.experienceService.updateExperience(experience.id, experience);
      this.loadItems();
      this.cancelAction();
    }
    this.loading = false;
  }

  confirmDelete(): void {
    this.cancelAction();
    this.loadItems();
  }

  cancelAction(): void {
    this.currentAction = 'list';
    this.isCreating = false;
    this.isUpdating = false;
    this.isDeleting = false;
    this.isViewing = false;
    this.selectedItem = null;
    this.detailItem = null;
    this.error = null;
    this.formData = {};
    this.stackInput = '';
  }

  goBack(): void {
    this.cancelAction();
  }

  getStatusClass(status: string): string {
    const statusClasses: { [key: string]: string } = {
      'Active': 'badge-success',
      'Stable': 'badge-info',
      'Deprecated': 'badge-warning',
      'Migrated': 'badge-default'
    };
    return statusClasses[status] || 'badge-default';
  }
}
<div class="model-dashboard">
  <!-- Back to Admin Button -->
  <div class="nav-header">
    <a routerLink="/management" class="btn btn-back">
      ← Back to Admin Dashboard
    </a>
  </div>

  <!-- Header -->
  <div class="dashboard-header">
    <h1>💼 {{ modelName }} Management</h1>
    <button 
      class="btn btn-primary" 
      (click)="createItem()"
      [disabled]="loading">
      + Add New {{ modelName }}
    </button>
  </div>

  <!-- Error Message -->
  <div *ngIf="error" class="alert alert-error">
    {{ error }}
    <button class="close-btn" (click)="error = null">×</button>
  </div>

  <!-- Loading Indicator -->
  <div *ngIf="loading" class="loading">
    <div class="spinner"></div>
    Loading...
  </div>

  <!-- List View -->
  <div *ngIf="currentAction === 'list' && !loading" class="list-view">
    <div *ngIf="items.length === 0" class="empty-state">
      <p>No {{ modelName | lowercase }}s found</p>
      <button class="btn btn-primary" (click)="createItem()">Create First {{ modelName }}</button>
    </div>

    <div class="items-grid" *ngIf="items.length > 0">
      <div class="item-card" *ngFor="let item of items" [class.selected]="selectedItem?.id === item.id">
        <div class="card-header">
          <span class="badge" [ngClass]="getStatusClass(item.status)">{{ item.status }}</span>
          <span class="period">{{ item.period }}</span>
        </div>
        <div class="card-body">
          <h3>{{ item.company }}</h3>
          <p class="role">{{ item.role }}</p>
          <p class="description">{{ item.description | slice:0:100 }}{{ item.description.length > 100 ? '...' : '' }}</p>
          <div class="tech-stack">
            <span class="tech-tag" *ngFor="let tech of item.stack | slice:0:4">{{ tech }}</span>
            <span class="tech-tag more" *ngIf="item.stack.length > 4">+{{ item.stack.length - 4 }}</span>
          </div>
        </div>
        <div class="card-metrics" *ngIf="item.metrics && item.metrics.length > 0">
          <div class="metric" *ngFor="let metric of item.metrics | slice:0:2">
            <span class="metric-label">{{ metric.label }}</span>
            <span class="metric-value" [ngClass]="'trend-' + metric.trend">{{ metric.value }}</span>
          </div>
        </div>
        <div class="card-actions">
          <button class="btn btn-sm btn-view" (click)="viewItem(item)">View</button>
          <button class="btn btn-sm btn-info" (click)="editItem(item)">Edit</button>
          <button class="btn btn-sm btn-danger" (click)="deleteItem(item)">Delete</button>
        </div>
      </div>
    </div>
  </div>

  <!-- Detail View -->
  <div *ngIf="isViewing && detailItem" class="detail-view">
    <div class="detail-header">
      <h2>{{ detailItem.company }}</h2>
      <span class="badge" [ngClass]="getStatusClass(detailItem.status)">{{ detailItem.status }}</span>
    </div>

    <div class="detail-content">
      <div class="detail-section">
        <label>Role</label>
        <p>{{ detailItem.role }}</p>
      </div>

      <div class="detail-section">
        <label>Period</label>
        <p>{{ detailItem.period }}</p>
      </div>

      <div class="detail-section">
        <label>Description</label>
        <p>{{ detailItem.description }}</p>
      </div>

      <div class="detail-section">
        <label>Tech Stack</label>
        <div class="tech-stack">
          <span class="tech-tag" *ngFor="let tech of detailItem.stack">{{ tech }}</span>
        </div>
      </div>

      <div class="detail-section" *ngIf="detailItem.metrics && detailItem.metrics.length > 0">
        <label>Metrics</label>
        <div class="metrics-grid">
          <div class="metric-card" *ngFor="let metric of detailItem.metrics">
            <span class="metric-label">{{ metric.label }}</span>
            <span class="metric-value" [ngClass]="'trend-' + metric.trend">{{ metric.value }}</span>
            <span class="trend-indicator" [ngClass]="'trend-' + metric.trend">
              {{ metric.trend === 'up' ? '↑' : metric.trend === 'down' ? '↓' : '→' }}
            </span>
          </div>
        </div>
      </div>
    </div>

    <div class="detail-actions">
      <button class="btn btn-info" (click)="editItem(detailItem)">Edit</button>
      <button class="btn btn-secondary" (click)="cancelAction()">Back to List</button>
    </div>
  </div>

  <!-- Create/Update Form -->
  <div *ngIf="(isCreating || isUpdating) && !loading" class="form-view">
    <div class="form-header">
      <h2>{{ isCreating ? 'Create' : 'Edit' }} {{ modelName }}</h2>
    </div>

    <form class="form">
      <div class="form-row">
        <div class="form-group">
          <label for="company">Company *</label>
          <input 
            id="company" 
            type="text" 
            class="form-control" 
            [(ngModel)]="formData.company"
            name="company"
            placeholder="e.g., Accenture"
            required>
        </div>

        <div class="form-group">
          <label for="role">Role *</label>
          <input 
            id="role" 
            type="text" 
            class="form-control" 
            [(ngModel)]="formData.role"
            name="role"
            placeholder="e.g., Senior DevOps Engineer"
            required>
        </div>
      </div>

      <div class="form-row">
        <div class="form-group">
          <label for="period">Period *</label>
          <input 
            id="period" 
            type="text" 
            class="form-control" 
            [(ngModel)]="formData.period"
            name="period"
            placeholder="e.g., 06/2022 - 08/2023">
        </div>

        <div class="form-group">
          <label for="status">Status</label>
          <select 
            id="status" 
            class="form-control" 
            [(ngModel)]="formData.status"
            name="status">
            <option *ngFor="let status of statusOptions" [value]="status">{{ status }}</option>
          </select>
        </div>
      </div>

      <div class="form-group">
        <label for="description">Description</label>
        <textarea 
          id="description" 
          class="form-control" 
          [(ngModel)]="formData.description"
          name="description"
          rows="4"
          placeholder="Describe the role and key achievements..."></textarea>
      </div>

      <div class="form-group">
        <label for="stack">Tech Stack (comma-separated)</label>
        <input 
          id="stack" 
          type="text" 
          class="form-control" 
          [(ngModel)]="stackInput"
          name="stack"
          placeholder="e.g., Java 21, Spring Boot, Kubernetes, Docker">
      </div>

      <div class="form-group">
        <label>Metrics</label>
        <div class="metrics-editor">
          <div class="metric-input-row">
            <input 
              type="text" 
              class="form-control" 
              [(ngModel)]="newMetric.label"
              name="metricLabel"
              placeholder="Label (e.g., Uptime)">
            <input 
              type="text" 
              class="form-control" 
              [(ngModel)]="newMetric.value"
              name="metricValue"
              placeholder="Value (e.g., 99.9%)">
            <select class="form-control" [(ngModel)]="newMetric.trend" name="metricTrend">
              <option value="up">↑ Up</option>
              <option value="down">↓ Down</option>
              <option value="stable">→ Stable</option>
            </select>
            <button type="button" class="btn btn-sm btn-primary" (click)="addMetric()">Add</button>
          </div>
          <div class="metrics-list" *ngIf="formData.metrics && formData.metrics.length > 0">
            <div class="metric-item" *ngFor="let metric of formData.metrics; let i = index">
              <span>{{ metric.label }}: {{ metric.value }} ({{ metric.trend }})</span>
              <button type="button" class="btn btn-sm btn-danger" (click)="removeMetric(i)">×</button>
            </div>
          </div>
        </div>
      </div>

      <div class="form-actions">
        <button type="button" class="btn btn-primary" (click)="saveItem()">Save</button>
        <button type="button" class="btn btn-secondary" (click)="cancelAction()">Cancel</button>
      </div>
    </form>
  </div>

  <!-- Delete Confirmation -->
  <div *ngIf="isDeleting && !loading" class="delete-confirmation">
    <div class="confirmation-dialog">
      <h2>Delete {{ modelName }}</h2>
      <p>Are you sure you want to delete this experience?</p>
      <p class="item-info">{{ selectedItem?.company }} - {{ selectedItem?.role }}</p>

      <div class="confirmation-actions">
        <button class="btn btn-danger" (click)="confirmDelete()">Delete</button>
        <button class="btn btn-secondary" (click)="cancelAction()">Cancel</button>
      </div>
    </div>
  </div>
</div>
