import { Injectable } from '@angular/core';
import { BehaviorSubject, Observable } from 'rxjs';

export interface UserProfile {
  name: string;
  title: string;
  email: string;
  phone?: string;
  location?: string;
  avatar?: string;
}

export interface AppSettings {
  verboseLogging: boolean;
  betaFeatures: boolean;
  theme: 'default' | 'high-contrast' | 'midnight';
}

@Injectable({
  providedIn: 'root'
})
export class UserService {
  private profile$ = new BehaviorSubject<UserProfile>({
    name: 'John Kojo Ampia-Addison',
    title: 'Senior Software Architect & DevOps Engineer',
    email: 'kojo.ampia@jojoaddison.net',
    phone: '+43 676 922 1796',
    location: 'Vienna, Austria',
    avatar: 'assets/kojo-ampia-addison.jpeg'
  });

  private settings$ = new BehaviorSubject<AppSettings>({
    verboseLogging: true,
    betaFeatures: false,
    theme: 'default'
  });

  constructor() {}

  getProfile(): Observable<UserProfile> {
    return this.profile$.asObservable();
  }

  updateProfile(profile: Partial<UserProfile>): void {
    const current = this.profile$.value;
    this.profile$.next({ ...current, ...profile });
  }

  getSettings(): Observable<AppSettings> {
    return this.settings$.asObservable();
  }

  updateSettings(settings: Partial<AppSettings>): void {
    const current = this.settings$.value;
    this.settings$.next({ ...current, ...settings });
  }
}
