import { Injectable } from '@angular/core';
import { Subscription, ReplaySubject, Subject } from 'rxjs';

import { AuthServerProvider } from './auth-jwt.service';
import { Location } from '@angular/common';

@Injectable({ providedIn: 'root' })
export class WebsocketAuthService {
  private routerSubscription: Subscription | null = null;
  private connectionSubject: ReplaySubject<void> = new ReplaySubject(1);
  MAX_RETRIES = 5;
  CURRENT_TRY = 0;

  authToken: string;

  constructor(private readonly authServerProvider: AuthServerProvider, private readonly location: Location) {
    this.authToken = this.authServerProvider.getToken();
  }

  // Get the connection
  public getConnection(): ReplaySubject<void> {
    return this.connectionSubject;
  }

  isConnected(): boolean {
    // WebSocket support removed - this service is stubbed for compatibility
    return false;
  }

  connect(): void {
    // WebSocket support removed - this service is stubbed for compatibility
    this.connectionSubject.next();
  }

  disconnect(): void {
    this.connectionSubject = new ReplaySubject(1);

    if (this.routerSubscription) {
      this.routerSubscription.unsubscribe();
      this.routerSubscription = null;
    }
  }

  subscribe(destination: string): Subject<any> {
    // WebSocket support removed - return a dummy subject
    return new Subject<any>();
  }
}
