import { Component, OnInit, signal, computed } from '@angular/core';
import { CommonModule } from '@angular/common';
import { EducationService } from '@app/core/services/education.service';
import { Education } from '@app/core/models/education.model';

@Component({
  selector: 'app-education',
  standalone: true,
  imports: [CommonModule],
  templateUrl: './education.component.html',
  styleUrls: ['./education.component.scss']
})
export class EducationComponent implements OnInit {
  education = signal<Education[]>([]);
  selectedType = signal<string>('All');

  educationTypes = computed(() => {
    const types = new Set(this.education().map(e => e.type));
    return ['All', ...Array.from(types)];
  });

  filteredEducation = computed(() => {
    if (this.selectedType() === 'All') {
      return this.education();
    }
    return this.education().filter(e => e.type === this.selectedType());
  });

  formalEducation = computed(() => 
    this.education().filter(e => 
      e.type.includes('University') || 
      e.type.includes('Secondary') || 
      e.type.includes('Pre University')
    )
  );

  professionalEducation = computed(() => 
    this.education().filter(e => 
      e.type.includes('Professional') || 
      e.type.includes('Workshop') ||
      e.type.includes('Certification')
    )
  );

  constructor(private educationService: EducationService) {}

  ngOnInit(): void {
    this.educationService.getAll().subscribe({
      next: (education) => {
        if (education && education.length > 0) {
          this.education.set(education);
        } else {
          this.loadMockEducation();
        }
      },
      error: () => {
        this.loadMockEducation();
      }
    });
  }

  loadMockEducation(): void {
    this.educationService.getEducation().subscribe(education => {
      this.education.set(education);
    });
  }

  filterByType(type: string): void {
    this.selectedType.set(type);
  }

  getTypeIcon(type: string): string {
    if (type.includes('University') || type.includes('Bachelor')) return 'fa-graduation-cap';
    if (type.includes('Pre University')) return 'fa-book-open';
    if (type.includes('Secondary')) return 'fa-school';
    if (type.includes('Professional') || type.includes('Workshop')) return 'fa-certificate';
    if (type.includes('Certification')) return 'fa-award';
    return 'fa-book';
  }

  getTypeColor(type: string): string {
    if (type.includes('University') || type.includes('Bachelor')) return 'text-purple-500';
    if (type.includes('Pre University')) return 'text-blue-500';
    if (type.includes('Secondary')) return 'text-green-500';
    if (type.includes('Professional') || type.includes('Workshop')) return 'text-orange-500';
    if (type.includes('Certification')) return 'text-yellow-500';
    return 'text-cyan-500';
  }

  getTypeBgColor(type: string): string {
    if (type.includes('University') || type.includes('Bachelor')) return 'bg-purple-500/10 border-purple-500/30';
    if (type.includes('Pre University')) return 'bg-blue-500/10 border-blue-500/30';
    if (type.includes('Secondary')) return 'bg-green-500/10 border-green-500/30';
    if (type.includes('Professional') || type.includes('Workshop')) return 'bg-orange-500/10 border-orange-500/30';
    if (type.includes('Certification')) return 'bg-yellow-500/10 border-yellow-500/30';
    return 'bg-cyan-500/10 border-cyan-500/30';
  }
}
<div class="space-y-8 animate-in fade-in slide-in-from-bottom-4 duration-500">
  <!-- Header Section -->
  <div class="flex flex-col md:flex-row md:justify-between md:items-center gap-4">
    <div>
      <h1 class="text-2xl font-bold text-slate-100 flex items-center gap-3">
        <i class="fas fa-graduation-cap text-yellow-500"></i>
        Education & Certifications
      </h1>
      <p class="text-sm text-slate-400 mt-1">Academic background and professional development journey</p>
    </div>

    <!-- Filter Tabs -->
    <div class="flex flex-wrap gap-2">
      @for (type of educationTypes(); track type) {
        <button 
          (click)="filterByType(type)"
          [ngClass]="selectedType() === type 
            ? 'bg-cyan-500/20 text-cyan-400 border-cyan-500/50' 
            : 'bg-slate-800 text-slate-400 border-slate-700 hover:border-slate-600'"
          class="px-3 py-1.5 text-xs font-medium rounded-lg border transition-all">
          {{ type === 'All' ? 'All' : (type.length > 20 ? type.substring(0, 20) + '...' : type) }}
        </button>
      }
    </div>
  </div>

  <!-- Stats Cards -->
  <div class="grid grid-cols-1 md:grid-cols-4 gap-4">
    <div class="bg-slate-800/50 border border-slate-700 p-4 rounded-lg hover:border-purple-500/50 transition-colors">
      <div class="text-slate-400 text-xs font-mono uppercase tracking-wider mb-1">Formal Education</div>
      <div class="text-2xl font-bold text-white">{{ formalEducation().length }}</div>
      <div class="text-xs text-purple-400 mt-1">Degrees & Certifications</div>
    </div>
    <div class="bg-slate-800/50 border border-slate-700 p-4 rounded-lg hover:border-orange-500/50 transition-colors">
      <div class="text-slate-400 text-xs font-mono uppercase tracking-wider mb-1">Professional</div>
      <div class="text-2xl font-bold text-white">{{ professionalEducation().length }}</div>
      <div class="text-xs text-orange-400 mt-1">Workshops & Training</div>
    </div>
    <div class="bg-slate-800/50 border border-slate-700 p-4 rounded-lg hover:border-cyan-500/50 transition-colors">
      <div class="text-slate-400 text-xs font-mono uppercase tracking-wider mb-1">Total Records</div>
      <div class="text-2xl font-bold text-white">{{ education().length }}</div>
      <div class="text-xs text-cyan-400 mt-1">Educational milestones</div>
    </div>
    <div class="bg-slate-800/50 border border-slate-700 p-4 rounded-lg hover:border-green-500/50 transition-colors">
      <div class="text-slate-400 text-xs font-mono uppercase tracking-wider mb-1">Years of Learning</div>
      <div class="text-2xl font-bold text-white">20+</div>
      <div class="text-xs text-green-400 mt-1">Continuous growth</div>
    </div>
  </div>

  <!-- Timeline View -->
  <div class="grid grid-cols-1 lg:grid-cols-2 gap-8">
    <!-- Formal Education Column -->
    <div class="space-y-4">
      <h2 class="text-lg font-bold text-slate-100 flex items-center gap-2 pb-2 border-b border-slate-800">
        <i class="fas fa-university text-purple-500"></i> Formal Education
      </h2>

      <div class="relative">
        <!-- Timeline Line -->
        <div class="absolute left-4 top-0 bottom-0 w-0.5 bg-gradient-to-b from-purple-500 via-blue-500 to-green-500"></div>

        <div class="space-y-6">
          @for (edu of formalEducation(); track edu.institution) {
            <div class="relative pl-10 group">
              <!-- Timeline Dot -->
              <div class="absolute left-2 top-2 w-5 h-5 rounded-full bg-slate-900 border-2 border-slate-700 group-hover:border-cyan-500 transition-colors flex items-center justify-center">
                <i class="fas {{ getTypeIcon(edu.type) }} text-[8px] {{ getTypeColor(edu.type) }}"></i>
              </div>

              <div class="bg-slate-900 border border-slate-800 rounded-lg p-5 hover:border-slate-700 transition-all hover:shadow-xl hover:shadow-black/30">
                <!-- Type Badge -->
                <div class="inline-flex px-2 py-1 rounded text-[10px] font-bold uppercase tracking-wider mb-3 border {{ getTypeBgColor(edu.type) }} {{ getTypeColor(edu.type) }}">
                  {{ edu.type }}
                </div>

                <h3 class="font-bold text-lg text-slate-100 group-hover:text-cyan-400 transition-colors">
                  {{ edu.institution }}
                </h3>

                <div class="flex items-center gap-2 mt-2 text-xs text-slate-500">
                  <i class="fas fa-calendar-alt"></i>
                  <span class="font-mono">{{ edu.duration }}</span>
                </div>

                <!-- Subjects -->
                <div class="mt-4">
                  <div class="text-[10px] uppercase text-slate-500 font-bold mb-2">Subjects / Focus Areas</div>
                  <div class="flex flex-wrap gap-2">
                    @for (subject of edu.subjects; track subject) {
                      <span class="text-[11px] px-2 py-1 rounded bg-slate-800/50 text-slate-300 border border-slate-700/50">
                        {{ subject }}
                      </span>
                    }
                  </div>
                </div>
              </div>
            </div>
          }
        </div>
      </div>
    </div>

    <!-- Professional Development Column -->
    <div class="space-y-4">
      <h2 class="text-lg font-bold text-slate-100 flex items-center gap-2 pb-2 border-b border-slate-800">
        <i class="fas fa-certificate text-orange-500"></i> Professional Development
      </h2>

      <div class="relative">
        <!-- Timeline Line -->
        <div class="absolute left-4 top-0 bottom-0 w-0.5 bg-gradient-to-b from-orange-500 via-yellow-500 to-amber-500"></div>

        <div class="space-y-6">
          @for (edu of professionalEducation(); track edu.institution + edu.duration) {
            <div class="relative pl-10 group">
              <!-- Timeline Dot -->
              <div class="absolute left-2 top-2 w-5 h-5 rounded-full bg-slate-900 border-2 border-slate-700 group-hover:border-orange-500 transition-colors flex items-center justify-center">
                <i class="fas {{ getTypeIcon(edu.type) }} text-[8px] {{ getTypeColor(edu.type) }}"></i>
              </div>

              <div class="bg-slate-900 border border-slate-800 rounded-lg p-5 hover:border-slate-700 transition-all hover:shadow-xl hover:shadow-black/30">
                <!-- Type Badge -->
                <div class="inline-flex px-2 py-1 rounded text-[10px] font-bold uppercase tracking-wider mb-3 border {{ getTypeBgColor(edu.type) }} {{ getTypeColor(edu.type) }}">
                  {{ edu.type }}
                </div>

                <h3 class="font-bold text-lg text-slate-100 group-hover:text-orange-400 transition-colors">
                  {{ edu.institution }}
                </h3>

                <div class="flex items-center gap-2 mt-2 text-xs text-slate-500">
                  <i class="fas fa-calendar-alt"></i>
                  <span class="font-mono">{{ edu.duration }}</span>
                </div>

                <!-- Topics -->
                <div class="mt-4">
                  <div class="text-[10px] uppercase text-slate-500 font-bold mb-2">Topics Covered</div>
                  <div class="flex flex-wrap gap-2">
                    @for (subject of edu.subjects; track subject) {
                      <span class="text-[11px] px-2 py-1 rounded bg-orange-500/10 text-orange-300 border border-orange-500/30">
                        {{ subject }}
                      </span>
                    }
                  </div>
                </div>
              </div>
            </div>
          }
        </div>
      </div>
    </div>
  </div>

  <!-- All Education Grid (shown when filtered) -->
  @if (selectedType() !== 'All') {
    <div class="space-y-4">
      <h2 class="text-lg font-bold text-slate-100 flex items-center gap-2">
        <i class="fas fa-filter text-cyan-500"></i> 
        Filtered Results: {{ selectedType() }}
        <span class="text-sm font-normal text-slate-500">({{ filteredEducation().length }} records)</span>
      </h2>

      <div class="grid grid-cols-1 md:grid-cols-2 xl:grid-cols-3 gap-4">
        @for (edu of filteredEducation(); track edu.institution + edu.duration) {
          <div class="bg-slate-900 border border-slate-800 rounded-lg p-5 hover:border-cyan-500/50 transition-all group">
            <div class="inline-flex px-2 py-1 rounded text-[10px] font-bold uppercase tracking-wider mb-3 border {{ getTypeBgColor(edu.type) }} {{ getTypeColor(edu.type) }}">
              {{ edu.type }}
            </div>

            <h3 class="font-bold text-slate-100 group-hover:text-cyan-400 transition-colors">
              {{ edu.institution }}
            </h3>

            <div class="text-xs text-slate-500 mt-2 font-mono">{{ edu.duration }}</div>

            <div class="flex flex-wrap gap-1.5 mt-3">
              @for (subject of edu.subjects.slice(0, 3); track subject) {
                <span class="text-[10px] px-2 py-0.5 rounded bg-slate-800 text-slate-400">{{ subject }}</span>
              }
              @if (edu.subjects.length > 3) {
                <span class="text-[10px] px-2 py-0.5 rounded bg-slate-800 text-slate-500">+{{ edu.subjects.length - 3 }} more</span>
              }
            </div>
          </div>
        }
      </div>
    </div>
  }
</div>
