    this.categoryOptions = ['Performance', 'Availability', 'Business', 'Security'];
    this.trendOptions = ['up', 'down', 'stable'];
    this.formData = {
      label: '',
      value: '',
      trend: 'stable',
      category: 'Performance',
      description: ''
    };
  }
  ngOnInit() {
    this.loadItems();
  }
  loadItems() {
    this.loading = true;
    this.metricService.getMetrics().subscribe({
      next: items => {
        this.items = items;
        this.filterByCategory();
        this.loading = false;
      },
      error: err => {
        this.error = 'Failed to load metrics: ' + err.message;
        this.loading = false;
      }
    });
  }
  filterByCategory() {
    if (this.selectedCategory === 'All') {
      this.filteredItems = [...this.items];
    } else {
      this.filteredItems = this.items.filter(m => m.category === this.selectedCategory);
    }
  }
  onCategoryFilterChange() {
    this.filterByCategory();
  }
  selectItem(item) {
    this.selectedItem = item;
  }
  viewItem(item) {
