import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { RouterModule } from '@angular/router';
import { MetricEntry, MetricService } from '@app/core/services/metric.service';

type MetricCategory = 'Performance' | 'Availability' | 'Business' | 'Security';
type TrendType = 'up' | 'down' | 'stable';

@Component({
  selector: 'app-metric-dashboard',
  standalone: true,
  imports: [CommonModule, FormsModule, RouterModule],
  templateUrl: './metric-dashboard.component.html',
  styleUrls: ['./metric-dashboard.component.scss']
})
export class MetricDashboardComponent implements OnInit {
  modelName = 'Metric';
  items: MetricEntry[] = [];
  filteredItems: MetricEntry[] = [];
  selectedItem: MetricEntry | null = null;
  detailItem: MetricEntry | null = null;
  isCreating = false;
  isUpdating = false;
  isDeleting = false;
  isViewing = false;
  currentAction: 'list' | 'create' | 'update' | 'delete' | 'view' = 'list';
  loading = false;
  error: string | null = null;
  selectedCategory: MetricCategory | 'All' = 'All';

  categoryOptions: MetricCategory[] = ['Performance', 'Availability', 'Business', 'Security'];
  trendOptions: TrendType[] = ['up', 'down', 'stable'];

  formData: Partial<MetricEntry> = {
    label: '',
    value: '',
    trend: 'stable',
    category: 'Performance',
    description: ''
  };

  constructor(private metricService: MetricService) {}

  ngOnInit(): void {
    this.loadItems();
  }

  loadItems(): void {
    this.loading = true;
    this.metricService.getMetrics().subscribe({
      next: (items: MetricEntry[]) => {
        this.items = items;
        this.filterByCategory();
        this.loading = false;
      },
      error: (err: any) => {
        this.error = 'Failed to load metrics: ' + err.message;
        this.loading = false;
      }
    });
  }

  filterByCategory(): void {
    if (this.selectedCategory === 'All') {
      this.filteredItems = [...this.items];
    } else {
      this.filteredItems = this.items.filter(m => m.category === this.selectedCategory);
    }
  }

  onCategoryFilterChange(): void {
    this.filterByCategory();
  }

  selectItem(item: MetricEntry): void {
    this.selectedItem = item;
  }

  viewItem(item: MetricEntry): void {
    this.currentAction = 'view';
    this.isViewing = true;
    this.detailItem = item;
    this.error = null;
  }

  createItem(): void {
    this.currentAction = 'create';
    this.isCreating = true;
    this.selectedItem = null;
    this.formData = {
      label: '',
      value: '',
      trend: 'stable',
      category: 'Performance',
      description: ''
    };
    this.error = null;
  }

  editItem(item: MetricEntry): void {
    this.currentAction = 'update';
    this.isUpdating = true;
    this.selectedItem = item;
    this.formData = { ...item };
    this.error = null;
  }

  deleteItem(item: MetricEntry): void {
    this.currentAction = 'delete';
    this.isDeleting = true;
    this.selectedItem = item;
    this.error = null;
  }

  saveItem(): void {
    this.loading = true;

    const metric = { ...this.selectedItem, ...this.formData } as MetricEntry;

    if (this.isCreating) {
      this.metricService.addMetric(metric);
      this.loadItems();
      this.cancelAction();
    } else if (this.isUpdating && metric.id) {
      this.metricService.updateMetric(metric.id, metric);
      this.loadItems();
      this.cancelAction();
    }
    this.loading = false;
  }

  confirmDelete(): void {
    if (this.selectedItem?.id) {
      this.metricService.deleteMetric(this.selectedItem.id);
      this.loadItems();
    }
    this.cancelAction();
  }

  cancelAction(): void {
    this.currentAction = 'list';
    this.isCreating = false;
    this.isUpdating = false;
    this.isDeleting = false;
    this.isViewing = false;
    this.selectedItem = null;
    this.detailItem = null;
    this.error = null;
    this.formData = {};
  }

  goBack(): void {
    this.cancelAction();
  }

  getCategoryClass(category: string): string {
    const categoryClasses: { [key: string]: string } = {
      'Performance': 'category-performance',
      'Availability': 'category-availability',
      'Business': 'category-business',
      'Security': 'category-security'
    };
    return categoryClasses[category] || '';
  }

  getTrendClass(trend: string): string {
    const trendClasses: { [key: string]: string } = {
      'up': 'trend-up',
      'down': 'trend-down',
      'stable': 'trend-stable'
    };
    return trendClasses[trend] || '';
  }

  getTrendIcon(trend: string): string {
    const icons: { [key: string]: string } = {
      'up': 'â†‘',
      'down': 'â†“',
      'stable': 'â†’'
    };
    return icons[trend] || 'â†’';
  }

  getCategoryIcon(category: string): string {
    const icons: { [key: string]: string } = {
      'Performance': 'âš¡',
      'Availability': 'ðŸŸ¢',
      'Business': 'ðŸ“ˆ',
      'Security': 'ðŸ”’'
    };
    return icons[category] || 'ðŸ“Š';
  }
}
<div class="model-dashboard">
  <!-- Back to Admin Button -->
  <div class="nav-header">
    <a routerLink="/management" class="btn btn-back">
      â† Back to Admin Dashboard
    </a>
  </div>

  <!-- Header -->
  <div class="dashboard-header">
    <h1>ðŸ“Š {{ modelName }} Management</h1>
    <div class="header-actions">
      <select class="form-control category-filter" [(ngModel)]="selectedCategory" (change)="onCategoryFilterChange()">
        <option value="All">All Categories</option>
        <option *ngFor="let cat of categoryOptions" [value]="cat">{{ cat }}</option>
      </select>
      <button 
        class="btn btn-primary" 
        (click)="createItem()"
        [disabled]="loading">
        + Add New {{ modelName }}
      </button>
    </div>
  </div>

  <!-- Error Message -->
  <div *ngIf="error" class="alert alert-error">
    {{ error }}
    <button class="close-btn" (click)="error = null">Ã—</button>
  </div>

  <!-- Loading Indicator -->
  <div *ngIf="loading" class="loading">
    <div class="spinner"></div>
    Loading...
  </div>

  <!-- List View -->
  <div *ngIf="currentAction === 'list' && !loading" class="list-view">
    <div *ngIf="filteredItems.length === 0" class="empty-state">
      <p>No {{ modelName | lowercase }}s found</p>
      <button class="btn btn-primary" (click)="createItem()">Create First {{ modelName }}</button>
    </div>

    <div class="metrics-grid" *ngIf="filteredItems.length > 0">
      <div class="metric-card" *ngFor="let item of filteredItems" 
           [ngClass]="getCategoryClass(item.category)"
           [class.selected]="selectedItem?.id === item.id">
        <div class="card-header">
          <span class="category-badge" [ngClass]="getCategoryClass(item.category)">
            {{ getCategoryIcon(item.category) }} {{ item.category }}
          </span>
        </div>
        <div class="card-body">
          <div class="metric-value-display">
            <span class="metric-value" [ngClass]="getTrendClass(item.trend)">{{ item.value }}</span>
            <span class="trend-indicator" [ngClass]="getTrendClass(item.trend)">{{ getTrendIcon(item.trend) }}</span>
          </div>
          <h3>{{ item.label }}</h3>
          <p class="description" *ngIf="item.description">{{ item.description }}</p>
        </div>
        <div class="card-actions">
          <button class="btn btn-sm btn-view" (click)="viewItem(item)">View</button>
          <button class="btn btn-sm btn-info" (click)="editItem(item)">Edit</button>
          <button class="btn btn-sm btn-danger" (click)="deleteItem(item)">Delete</button>
        </div>
      </div>
    </div>
  </div>

  <!-- Detail View -->
  <div *ngIf="isViewing && detailItem" class="detail-view">
    <div class="detail-header">
      <span class="category-badge" [ngClass]="getCategoryClass(detailItem.category)">
        {{ getCategoryIcon(detailItem.category) }} {{ detailItem.category }}
      </span>
      <h2>{{ detailItem.label }}</h2>
    </div>

    <div class="detail-content">
      <div class="metric-display">
        <div class="big-value" [ngClass]="getTrendClass(detailItem.trend)">
          {{ detailItem.value }}
          <span class="trend-icon">{{ getTrendIcon(detailItem.trend) }}</span>
        </div>
        <div class="trend-label" [ngClass]="getTrendClass(detailItem.trend)">
          Trend: {{ detailItem.trend | titlecase }}
        </div>
      </div>

      <div class="detail-section" *ngIf="detailItem.description">
        <label>Description</label>
        <p>{{ detailItem.description }}</p>
      </div>

      <div class="detail-section">
        <label>Category</label>
        <p>{{ detailItem.category }}</p>
      </div>
    </div>

    <div class="detail-actions">
      <button class="btn btn-info" (click)="editItem(detailItem)">Edit</button>
      <button class="btn btn-secondary" (click)="cancelAction()">Back to List</button>
    </div>
  </div>

  <!-- Create/Update Form -->
  <div *ngIf="(isCreating || isUpdating) && !loading" class="form-view">
    <div class="form-header">
      <h2>{{ isCreating ? 'Create' : 'Edit' }} {{ modelName }}</h2>
    </div>

    <form class="form">
      <div class="form-row">
        <div class="form-group">
          <label for="label">Metric Label *</label>
          <input 
            id="label" 
            type="text" 
            class="form-control" 
            [(ngModel)]="formData.label"
            name="label"
            placeholder="e.g., API Response Time"
            required>
        </div>

        <div class="form-group">
          <label for="value">Value *</label>
          <input 
            id="value" 
            type="text" 
            class="form-control" 
            [(ngModel)]="formData.value"
            name="value"
            placeholder="e.g., 99.9%, <50ms, A+"
            required>
        </div>
      </div>

      <div class="form-row">
        <div class="form-group">
          <label for="category">Category</label>
          <select 
            id="category" 
            class="form-control" 
            [(ngModel)]="formData.category"
            name="category">
            <option *ngFor="let cat of categoryOptions" [value]="cat">{{ cat }}</option>
          </select>
        </div>

        <div class="form-group">
          <label for="trend">Trend</label>
          <select 
            id="trend" 
            class="form-control" 
            [(ngModel)]="formData.trend"
            name="trend">
            <option value="up">â†‘ Up (Positive)</option>
            <option value="down">â†“ Down (Negative)</option>
            <option value="stable">â†’ Stable</option>
          </select>
        </div>
      </div>

      <div class="form-group">
        <label for="description">Description</label>
        <textarea 
          id="description" 
          class="form-control" 
          [(ngModel)]="formData.description"
          name="description"
          rows="3"
          placeholder="Describe what this metric measures..."></textarea>
      </div>

      <div class="form-actions">
        <button type="button" class="btn btn-primary" (click)="saveItem()">Save</button>
        <button type="button" class="btn btn-secondary" (click)="cancelAction()">Cancel</button>
      </div>
    </form>
  </div>

  <!-- Delete Confirmation -->
  <div *ngIf="isDeleting && !loading" class="delete-confirmation">
    <div class="confirmation-dialog">
      <h2>Delete {{ modelName }}</h2>
      <p>Are you sure you want to delete this metric?</p>
      <p class="item-info">{{ selectedItem?.label }}: {{ selectedItem?.value }}</p>

      <div class="confirmation-actions">
        <button class="btn btn-danger" (click)="confirmDelete()">Delete</button>
        <button class="btn btn-secondary" (click)="cancelAction()">Cancel</button>
      </div>
    </div>
  </div>
</div>
