import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { RouterModule } from '@angular/router';
import { Project, ProjectType, ProjectStatus } from '@app/core/models/project.model';
import { ProjectService } from '@app/core/services/project.service';

@Component({
  selector: 'app-project-dashboard',
  standalone: true,
  imports: [CommonModule, FormsModule, RouterModule],
  templateUrl: './project-dashboard.component.html',
  styleUrls: ['./project-dashboard.component.scss']
})
export class ProjectDashboardComponent implements OnInit {
  modelName = 'Project';
  items: Project[] = [];
  selectedItem: Project | null = null;
  detailItem: Project | null = null;
  isCreating = false;
  isUpdating = false;
  isDeleting = false;
  isViewing = false;
  currentAction: 'list' | 'create' | 'update' | 'delete' | 'view' = 'list';
  loading = false;
  error: string | null = null;

  typeOptions: ProjectType[] = ['Microservices', 'Monolith', 'Migration', 'DevOps', 'Architecture', 'ETL', 'Monitoring'];
  statusOptions: ProjectStatus[] = ['Live', 'Completed', 'Maintenance', 'Pending'];

  formData: Partial<Project> = {
    name: '',
    client: '',
    type: 'Microservices',
    description: '',
    stack: [],
    status: 'Pending',
    architecture: ''
  };

  stackInput = '';

  constructor(private projectService: ProjectService) {}

  ngOnInit(): void {
    this.loadItems();
  }

  loadItems(): void {
    this.loading = true;
    this.projectService.getProjects().subscribe({
      next: (items: Project[]) => {
        this.items = items;
        this.loading = false;
      },
      error: (err: any) => {
        this.error = 'Failed to load projects: ' + err.message;
        this.loading = false;
      }
    });
  }

  selectItem(item: Project): void {
    this.selectedItem = item;
  }

  viewItem(item: Project): void {
    this.currentAction = 'view';
    this.isViewing = true;
    this.detailItem = item;
    this.error = null;
  }

  createItem(): void {
    this.currentAction = 'create';
    this.isCreating = true;
    this.selectedItem = null;
    this.stackInput = '';
    this.formData = {
      name: '',
      client: '',
      type: 'Microservices',
      description: '',
      stack: [],
      status: 'Pending',
      architecture: ''
    };
    this.error = null;
  }

  editItem(item: Project): void {
    this.currentAction = 'update';
    this.isUpdating = true;
    this.selectedItem = item;
    this.formData = { ...item, stack: [...item.stack] };
    this.stackInput = item.stack.join(', ');
    this.error = null;
  }

  deleteItem(item: Project): void {
    this.currentAction = 'delete';
    this.isDeleting = true;
    this.selectedItem = item;
    this.error = null;
  }

  saveItem(): void {
    this.loading = true;

    // Parse stack from comma-separated input
    this.formData.stack = this.stackInput.split(',').map(s => s.trim()).filter(s => s);

    const project = { ...this.selectedItem, ...this.formData } as Project;

    if (this.isCreating) {
      this.projectService.addProject(project);
      this.loadItems();
      this.cancelAction();
    } else if (this.isUpdating && project.id) {
      this.projectService.updateProject(project.id, project);
      this.loadItems();
      this.cancelAction();
    }
    this.loading = false;
  }

  confirmDelete(): void {
    this.cancelAction();
    this.loadItems();
  }

  cancelAction(): void {
    this.currentAction = 'list';
    this.isCreating = false;
    this.isUpdating = false;
    this.isDeleting = false;
    this.isViewing = false;
    this.selectedItem = null;
    this.detailItem = null;
    this.error = null;
    this.formData = {};
    this.stackInput = '';
  }

  goBack(): void {
    this.cancelAction();
  }

  getStatusClass(status: string): string {
    const statusClasses: { [key: string]: string } = {
      'Live': 'badge-success',
      'Completed': 'badge-info',
      'Maintenance': 'badge-warning',
      'Pending': 'badge-default'
    };
    return statusClasses[status] || 'badge-default';
  }

  getTypeClass(type: string): string {
    const typeClasses: { [key: string]: string } = {
      'Microservices': 'type-microservices',
      'Monolith': 'type-monolith',
      'Migration': 'type-migration',
      'DevOps': 'type-devops',
      'Architecture': 'type-architecture',
      'ETL': 'type-etl',
      'Monitoring': 'type-monitoring'
    };
    return typeClasses[type] || '';
  }
}
<div class="model-dashboard">
  <!-- Back to Admin Button -->
  <div class="nav-header">
    <a routerLink="/management" class="btn btn-back">
      ← Back to Admin Dashboard
    </a>
  </div>

  <!-- Header -->
  <div class="dashboard-header">
    <h1>📁 {{ modelName }} Management</h1>
    <button 
      class="btn btn-primary" 
      (click)="createItem()"
      [disabled]="loading">
      + Add New {{ modelName }}
    </button>
  </div>

  <!-- Error Message -->
  <div *ngIf="error" class="alert alert-error">
    {{ error }}
    <button class="close-btn" (click)="error = null">×</button>
  </div>

  <!-- Loading Indicator -->
  <div *ngIf="loading" class="loading">
    <div class="spinner"></div>
    Loading...
  </div>

  <!-- List View -->
  <div *ngIf="currentAction === 'list' && !loading" class="list-view">
    <div *ngIf="items.length === 0" class="empty-state">
      <p>No {{ modelName | lowercase }}s found</p>
      <button class="btn btn-primary" (click)="createItem()">Create First {{ modelName }}</button>
    </div>

    <div class="items-grid" *ngIf="items.length > 0">
      <div class="item-card" *ngFor="let item of items" [class.selected]="selectedItem?.id === item.id">
        <div class="card-header">
          <span class="badge" [ngClass]="getStatusClass(item.status)">{{ item.status }}</span>
          <span class="type-badge" [ngClass]="getTypeClass(item.type)">{{ item.type }}</span>
        </div>
        <div class="card-body">
          <h3>{{ item.name }}</h3>
          <p class="client">{{ item.client }}</p>
          <p class="description">{{ item.description | slice:0:120 }}{{ item.description.length > 120 ? '...' : '' }}</p>
          <div class="architecture" *ngIf="item.architecture">
            <span class="arch-label">Architecture:</span>
            <span class="arch-value">{{ item.architecture }}</span>
          </div>
          <div class="tech-stack">
            <span class="tech-tag" *ngFor="let tech of item.stack | slice:0:4">{{ tech }}</span>
            <span class="tech-tag more" *ngIf="item.stack.length > 4">+{{ item.stack.length - 4 }}</span>
          </div>
        </div>
        <div class="card-actions">
          <button class="btn btn-sm btn-view" (click)="viewItem(item)">View</button>
          <button class="btn btn-sm btn-info" (click)="editItem(item)">Edit</button>
          <button class="btn btn-sm btn-danger" (click)="deleteItem(item)">Delete</button>
        </div>
      </div>
    </div>
  </div>

  <!-- Detail View -->
  <div *ngIf="isViewing && detailItem" class="detail-view">
    <div class="detail-header">
      <h2>{{ detailItem.name }}</h2>
      <div class="header-badges">
        <span class="badge" [ngClass]="getStatusClass(detailItem.status)">{{ detailItem.status }}</span>
        <span class="type-badge" [ngClass]="getTypeClass(detailItem.type)">{{ detailItem.type }}</span>
      </div>
    </div>

    <div class="detail-content">
      <div class="detail-section">
        <label>Client</label>
        <p>{{ detailItem.client }}</p>
      </div>

      <div class="detail-section">
        <label>Architecture</label>
        <p>{{ detailItem.architecture }}</p>
      </div>

      <div class="detail-section">
        <label>Description</label>
        <p>{{ detailItem.description }}</p>
      </div>

      <div class="detail-section">
        <label>Tech Stack</label>
        <div class="tech-stack">
          <span class="tech-tag" *ngFor="let tech of detailItem.stack">{{ tech }}</span>
        </div>
      </div>
    </div>

    <div class="detail-actions">
      <button class="btn btn-info" (click)="editItem(detailItem)">Edit</button>
      <button class="btn btn-secondary" (click)="cancelAction()">Back to List</button>
    </div>
  </div>

  <!-- Create/Update Form -->
  <div *ngIf="(isCreating || isUpdating) && !loading" class="form-view">
    <div class="form-header">
      <h2>{{ isCreating ? 'Create' : 'Edit' }} {{ modelName }}</h2>
    </div>

    <form class="form">
      <div class="form-row">
        <div class="form-group">
          <label for="name">Project Name *</label>
          <input 
            id="name" 
            type="text" 
            class="form-control" 
            [(ngModel)]="formData.name"
            name="name"
            placeholder="e.g., Health Brokerage Platform"
            required>
        </div>

        <div class="form-group">
          <label for="client">Client *</label>
          <input 
            id="client" 
            type="text" 
            class="form-control" 
            [(ngModel)]="formData.client"
            name="client"
            placeholder="e.g., Abofonsa Mobile Health"
            required>
        </div>
      </div>

      <div class="form-row">
        <div class="form-group">
          <label for="type">Project Type</label>
          <select 
            id="type" 
            class="form-control" 
            [(ngModel)]="formData.type"
            name="type">
            <option *ngFor="let type of typeOptions" [value]="type">{{ type }}</option>
          </select>
        </div>

        <div class="form-group">
          <label for="status">Status</label>
          <select 
            id="status" 
            class="form-control" 
            [(ngModel)]="formData.status"
            name="status">
            <option *ngFor="let status of statusOptions" [value]="status">{{ status }}</option>
          </select>
        </div>
      </div>

      <div class="form-group">
        <label for="architecture">Architecture</label>
        <input 
          id="architecture" 
          type="text" 
          class="form-control" 
          [(ngModel)]="formData.architecture"
          name="architecture"
          placeholder="e.g., Event-Driven, Cloud Native, Microservices">
      </div>

      <div class="form-group">
        <label for="description">Description</label>
        <textarea 
          id="description" 
          class="form-control" 
          [(ngModel)]="formData.description"
          name="description"
          rows="4"
          placeholder="Describe the project scope and key deliverables..."></textarea>
      </div>

      <div class="form-group">
        <label for="stack">Tech Stack (comma-separated)</label>
        <input 
          id="stack" 
          type="text" 
          class="form-control" 
          [(ngModel)]="stackInput"
          name="stack"
          placeholder="e.g., Java 17, Kafka, MongoDB, Angular 17">
      </div>

      <div class="form-actions">
        <button type="button" class="btn btn-primary" (click)="saveItem()">Save</button>
        <button type="button" class="btn btn-secondary" (click)="cancelAction()">Cancel</button>
      </div>
    </form>
  </div>

  <!-- Delete Confirmation -->
  <div *ngIf="isDeleting && !loading" class="delete-confirmation">
    <div class="confirmation-dialog">
      <h2>Delete {{ modelName }}</h2>
      <p>Are you sure you want to delete this project?</p>
      <p class="item-info">{{ selectedItem?.name }} ({{ selectedItem?.client }})</p>

      <div class="confirmation-actions">
        <button class="btn btn-danger" (click)="confirmDelete()">Delete</button>
        <button class="btn btn-secondary" (click)="cancelAction()">Cancel</button>
      </div>
    </div>
  </div>
</div>
