import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterModule } from '@angular/router';

export interface DashboardMenuItem {
  title: string;
  description: string;
  icon: string;
  emoji: string;
  route: string;
  color: string;
}

@Component({
  selector: 'app-admin-dashboard',
  standalone: true,
  imports: [CommonModule, RouterModule],
  templateUrl: './admin-dashboard.component.html',
  styleUrls: ['./admin-dashboard.component.scss']
})
export class AdminDashboardComponent implements OnInit {
  menuItems: DashboardMenuItem[] = [];
  iconMap: { [key: string]: string } = {
    person: 'üë§',
    settings: '‚öôÔ∏è',
    description: 'üìÑ',
    school: 'üéì',
    work: 'üíº',
    folder: 'üìÅ',
    mail: 'üìß',
    show_chart: 'üìä'
  };

  ngOnInit(): void {
    this.initializeMenuItems();
  }

  private initializeMenuItems(): void {
    this.menuItems = [
      {
        title: 'Profiles',
        description: 'Manage user profiles',
        icon: 'person',
        emoji: 'üë§',
        route: '/management/profiles',
        color: 'primary'
      },
      {
        title: 'Settings',
        description: 'Manage application settings',
        icon: 'settings',
        emoji: '‚öôÔ∏è',
        route: '/management/settings',
        color: 'accent'
      },
      {
        title: 'Documentation',
        description: 'Manage documentation',
        icon: 'description',
        emoji: 'üìÑ',
        route: '/management/documentation',
        color: 'warn'
      },
      {
        title: 'Skills',
        description: 'Manage technical skills',
        icon: 'school',
        emoji: 'üéì',
        route: '/management/skills',
        color: 'primary'
      },
      {
        title: 'Experience',
        description: 'Manage work experience',
        icon: 'work',
        emoji: 'üíº',
        route: '/management/experience',
        color: 'accent'
      },
      {
        title: 'Projects',
        description: 'Manage projects',
        icon: 'folder',
        emoji: 'üìÅ',
        route: '/management/projects',
        color: 'warn'
      },
      {
        title: 'Inquiries',
        description: 'Manage inquiries',
        icon: 'mail',
        emoji: 'üìß',
        route: '/management/inquiries',
        color: 'primary'
      },
      {
        title: 'Metrics',
        description: 'View system metrics',
        icon: 'show_chart',
        emoji: 'üìä',
        route: '/management/metrics',
        color: 'accent'
      }
    ];
  }
}
<div class="admin-dashboard">
  <div class="dashboard-header">
    <h1>Administration Dashboard</h1>
    <p>Manage all system models and configurations</p>
  </div>

  <div class="menu-grid">
    <div *ngFor="let item of menuItems" class="menu-card" [ngClass]="'card-' + item.color">
      <a [routerLink]="item.route" class="card-link">
        <div class="card-icon">
          <mat-icon *ngIf="item.icon" class="icon">{{ item.icon }}</mat-icon>
          <span *ngIf="!item.icon" class="icon-text">{{ item.title.charAt(0) }}</span>
        </div>
        <div class="card-content">
          <h3>{{ item.title }}</h3>
          <p>{{ item.description }}</p>
        </div>
        <div class="card-arrow">
          <mat-icon>arrow_forward</mat-icon>
        </div>
      </a>
    </div>
  </div>
</div>
